INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 3, 0, 'If you\'ve a way out of here, we should probably use it! I\'ve tried casting myself and Tyrande out of here, but the well is too much in flux!', 12, 0, 100, 0, 0, 26062, 'woe illidan conclusion 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 2, 0, 'Aye. It\'s been twisted and turned by too many spells. The fuss we - especially you - made with the portal was too much! The same spell that sent the Burning Legion back into their foul realm now works on the well! It\'s devouring itself and taking its surroundings with it! Fascinating, isn\'t it?', 12, 0, 100, 0, 0, 26061, 'woe illidan conclusion 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 0, 0, 'The artifact!', 14, 0, 100, 0, 0, 26059, 'woe illidan conclusion 1');
