INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55570, 2, 0, 'Not if we\'re caught up in it! Why weren\'t you running? What have you been doing with your hand in the Well?', 12, 0, 100, 0, 0, 26492, 'woe malfurion conclusion 03');
