INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 18, 0, 'The Divine will not suffer your taint here, outsider.', 14, 0, 100, 0, 0, 29318, 'VO_HOF_ZORLOK_SLAY_01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 19, 0, 'Ours is but to serve; yours is but to die.', 14, 0, 100, 0, 0, 29319, 'VO_HOF_ZORLOK_SLAY_02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 17, 0, 'And so it falls to us, Her chosen voice.', 14, 0, 100, 0, 0, 29317, 'VO_HOF_ZORLOK_EVENT_TRASH_C_DIES');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 16, 0, 'The Divine challenges us to face these intruders.', 14, 0, 100, 0, 0, 29316, 'VO_HOF_ZORLOK_EVENT_TRASH_C_COMBAT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 15, 0, 'We are unfazed. We will stand firm.', 14, 0, 100, 0, 0, 29315, 'VO_HOF_ZORLOK_EVENT_TRASH_B_DIES');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 14, 0, 'They are but the waves crashing upon the mountain of Her divine will. They may rise again and again; but will accomplish nothing.', 14, 0, 100, 0, 0, 29314, 'VO_HOF_ZORLOK_EVENT_TRASH_B_COMBAT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 13, 0, 'They were clearly unworthy of Her divine embrace.', 14, 0, 100, 0, 0, 29313, 'VO_HOF_ZORLOK_EVENT_TRASH_A_DIES');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 12, 0, 'The chaff of the world tumbles across our doorstep, driven by fear; Her royal swarm will whisk them away.', 14, 0, 100, 0, 0, 29312, 'VO_HOF_ZORLOK_EVENT_TRASH_A_COMBAT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 10, 0, 'She tests us. We will not disappoint Her.', 14, 0, 100, 0, 0, 29310, 'VO_HOF_ZORLOK_EVENT_PHASE2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 11, 0, 'We are as unwavering as the amber that shapes our halls. With Her might we will vanquish all who dare intrude upon Her divine home.', 14, 0, 100, 0, 0, 29311, 'VO_HOF_ZORLOK_EVENT_PHASE3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 9, 0, 'Her gifts are many, our gratitude immeasurable.', 14, 0, 100, 0, 0, 29309, 'VO_HOF_ZORLOK_EVENT_PHASE1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 7, 0, 'Her happiness is our reward, her sorrow our failure.', 14, 0, 100, 0, 0, 29307, 'VO_HOF_ZORLOK_EVENT_IDLE_3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 8, 0, 'We will give our lives for the Empress without hesitation. She is the light, and without Her our lives will be lost to darkness.', 14, 0, 100, 0, 0, 29308, 'VO_HOF_ZORLOK_EVENT_IDLE_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 6, 0, 'We fight, toil, and serve so that Her vision for us is made reality.', 14, 0, 100, 0, 0, 29306, 'VO_HOF_ZORLOK_EVENT_IDLE_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 5, 0, 'Never to question, nor to contemplate; we simply act.', 14, 0, 100, 0, 0, 29305, 'VO_HOF_ZORLOK_EVENT_IDLE_1C');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 4, 0, 'Ours is but to serve in Her divine name.', 14, 0, 100, 0, 0, 29304, 'VO_HOF_ZORLOK_EVENT_IDLE_1B');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 3, 0, 'We are the extension of our Empress\'s will.', 14, 0, 100, 0, 0, 29303, 'VO_HOF_ZORLOK_EVENT_IDLE_1A');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 2, 0, 'We will not give in to the despair of the dark void. If Her will for us is to perish, then it shall be so.', 14, 0, 100, 0, 0, 29302, 'VO_HOF_ZORLOK_DEATH_01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 1, 0, 'The Divine chose us to give mortal voice to Her divine will. We are but the vessel that enacts Her will.', 14, 0, 100, 0, 0, 29301, 'VO_HOF_ZORLOK_AGGRO_01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 20, 0, 'Behold the voice of Her divine wrath.', 14, 0, 100, 0, 0, 29320, 'VO_HOF_ZORLOK_SPELL_EXHALE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 21, 0, 'Through the air we breathe, She strengthens our voice.', 14, 0, 100, 0, 0, 29321, 'VO_HOF_ZORLOK_SPELL_INHALE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 22, 0, 'Her will is greater than us all. You will join us or you will perish.', 14, 0, 100, 0, 0, 29322, 'VO_HOF_ZORLOK_SPELL_MINDCONTROL');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62980, 23, 0, 'Our voice gives form to Her will.', 14, 0, 100, 0, 0, 29323, 'VO_HOF_ZORLOK_SPELL_PITCH');
