INSERT INTO `script_texts` VALUES ('54586', '-5458607', 'I have never seen a trainee with skills such as yours. I must tell the others.', 'Je n\'ai jamais vu une recrue avant autant d\'adresse que vous. Je dois prévenir les autres.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458606', 'My skills are no match for yours. I admit defeat.', 'Mes capacites n\'egalent pas les votres. J\'admets ma defaite.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458605', 'Thank you for reminding me that I must train more diligently.', 'Merci de m\'avoir rappele que je dois m\'entraîner avec discernement.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458604', 'That was a good match. Thank you.', 'C\'etait un bon combat. Merci a vous.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458603', 'You are an honorable opponent.', 'Vous êtes un adversaire honorable.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458602', 'You fight honorably, friend.', 'Vous avez combattu honorablement mon ami.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458601', 'You fought well. I must learn more from you in the future.', 'Vous vous êtes bien battu. Je dois apprendre plus de vous dans l\'avenir.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
INSERT INTO `script_texts` VALUES ('54586', '-5458600', 'Your skills are too great. I yield.', 'Vos capacites sont impressionnantes. Je m\'incline.', null, null, null, null, null, null, null, null, null, '0', '12', '0', '0', 'Trainee - defeated');
