INSERT INTO `creature_text` VALUES (62980, 0, 0, 'The divine chose us to give mortal voice to Her divine will. We are but the vessel that enacts Her will.', 14, 0, 100, 0, 2, 29301, 'say 1 aggro');
INSERT INTO `creature_text` VALUES (62980, 1, 0, 'We will not give in to the despair of the dark void. If Her will for us is to perish, then it shall be so.', 14, 0, 100, 0, 2, 29302, 'say 2 death');
INSERT INTO `creature_text` VALUES (62980, 2, 0, 'We are the extension of our Empress\'\s will.', 14, 0, 100, 0, 2, 29303, 'say 3 event 1a');
INSERT INTO `creature_text` VALUES (62980, 3, 0, 'Ours is but to serve in Her divine name.', 14, 0, 100, 0, 2, 29304, 'say 4 event idle 1b');
INSERT INTO `creature_text` VALUES (62980, 4, 0, 'Never to question, nor to contemplate; we simply act.', 14, 0, 100, 0, 2, 29305, 'say 5 idle 1c');
INSERT INTO `creature_text` VALUES (62980, 5, 0, 'We fight, toil, and serve so that Her vision for us is made reality.', 14, 0, 100, 0, 2, 29306, 'say 6 event idle 2');
INSERT INTO `creature_text` VALUES (62980, 6, 0, 'Her happiness is our reward, her sorrow our failure.', 14, 0, 100, 0, 2, 29307, 'say 7 event idle 3');
INSERT INTO `creature_text` VALUES (62980, 7, 0, 'We will give our lives for the Empress without hesitation. She is the light, and without Her our lives will be lost to darkness.', 14, 0, 100, 0, 2, 29308, 'say 8 idle 4');
INSERT INTO `creature_text` VALUES (62980, 8, 0, 'Her gifts are many, our gratitude immeasurable.', 14, 0, 100, 0, 2, 29309, 'say 9 phase 1');
INSERT INTO `creature_text` VALUES (62980, 9, 0, 'She tests us. We will not disappoint Her.', 14, 0, 100, 0, 2, 29310, 'say 10 phase 2');
INSERT INTO `creature_text` VALUES (62980, 10, 0, 'We are as unwavering as the amber that shapes our halls. With Her might we will vanquish all who dare intrude upon Her divine home.', 14, 0, 100, 0, 2, 29311, 'say 11 phase 3');
INSERT INTO `creature_text` VALUES (62980, 11, 0, 'The chaff of the world tumbles across our doorstep, driven by fear; Her royal swarm will whisk them away.', 14, 0, 100, 0, 2, 29312, 'say 12 a combat');
INSERT INTO `creature_text` VALUES (62980, 12, 0, 'They will clearly unworthy of her devine embrace.', 14, 0, 100, 0, 2, 29313, 'say 13 a dies');
INSERT INTO `creature_text` VALUES (62980, 13, 0, 'They are but the waves crashing upon the mountain of Her divine will. They may rise again and again; but will accomplish nothing.', 14, 0, 100, 0, 2, 29314, 'say 14 b combat');
INSERT INTO `creature_text` VALUES (62980, 14, 0, 'We are unfazed. We will stand firm.', 14, 0, 100, 0, 2, 29315, 'say 15 b dies');
INSERT INTO `creature_text` VALUES (62980, 15, 0, 'The Divine challenges us to face these intruders.', 14, 0, 100, 0, 2, 29316, 'say 16 c combat');
INSERT INTO `creature_text` VALUES (62980, 16, 0, 'And so it falls to us, Her chosen voice.', 14, 0, 100, 0, 2, 29317, 'say 17 c dies');
INSERT INTO `creature_text` VALUES (62980, 17, 0, 'The divine will not suffer your taint here, outsider!', 14, 0, 100, 0, 2, 29318, 'say 18 slay 1');
INSERT INTO `creature_text` VALUES (62980, 18, 0, 'Ours is but to serve, yours is but to die!', 14, 0, 100, 0, 2, 29319, 'say 19 slay 2');
INSERT INTO `creature_text` VALUES (62980, 19, 0, 'Behold the voice of Her divine wrath.', 14, 0, 100, 0, 2, 29320, 'say 20 exhale');
INSERT INTO `creature_text` VALUES (62980, 20, 0, 'Through the air we breathe. She strengthens our voice.', 14, 0, 100, 0, 2, 29321, 'say 21 inhale');
INSERT INTO `creature_text` VALUES (62980, 21, 0, 'Her will is greater than us all. You will join us or you will perish.', 14, 0, 100, 0, 2, 29322, 'say 22 mindcontrol');
INSERT INTO `creature_text` VALUES (62980, 22, 0, 'Our voice gives form to Her will.', 14, 0, 100, 0, 2, 29323, 'say 23 pitch');
