INSERT INTO `creature_text` VALUES (62543, 0, 0, 'On your guard, invaders. I, Ta\'\yak, Lord of Blades, will be your opponent.', 14, 0, 100, 0, 2, 29252, 'say 1 aggro');
INSERT INTO `creature_text` VALUES (62543, 1, 0, 'I\'\d always hoped to cut someone like that someday, to hear that sound. But to have it happen to my own prothorax is ridiculous.', 14, 0, 100, 0, 2, 29253, 'say 2 death');
INSERT INTO `creature_text` VALUES (62543, 3, 0, 'Mere initiates.', 14, 0, 100, 0, 2, 29254, 'say 3 killing trash');
INSERT INTO `creature_text` VALUES (62543, 4, 0, 'They approach? Good. Now, if only my students were here to see and learn from the coming display of martial prowess...', 14, 0, 100, 0, 2, 29258, 'say 4 intro');
INSERT INTO `creature_text` VALUES (62543, 5, 0, 'A perfect cut.', 14, 0, 100, 0, 2, 29259, 'say 5 slay');
INSERT INTO `creature_text` VALUES (62543, 6, 0, 'Can you follow my blade?', 14, 0, 100, 0, 2, 29261, 'say 6 UNLEASHED');
INSERT INTO `creature_text` VALUES (62543, 7, 0, 'Blade Lord Ta\'\yak marks $N for [Unseen Strike]!', 12, 0, 100, 0, 2, 29254, 'say 7 unseen');
INSERT INTO `creature_text` VALUES (62543, 8, 0, 'Now go, impart my techniques to the initiates.', 14, 0, 100, 0, 2, 29260, 'say enter the room');
INSERT INTO `creature_text` VALUES (62164, 0, 0, 'Garalon begins to $N [Crush] his opponents!', 12, 0, 100, 0, 2, 32155, 'say 1 crush');
INSERT INTO `creature_text` VALUES (62164, 1, 0, 'Garalon $N [Mends] one of his legs!', 12, 0, 100, 0, 2, 32156, 'say 1 mend');
INSERT INTO `creature_text` VALUES (62164, 2, 0, 'Garalon fails to hit at least two players and gains $N [Fury]!', 12, 0, 100, 0, 2, 32157, 'say 1 fury');
INSERT INTO `creature_text` VALUES (62164, 3, 0, 'Garalon\'\s massive armor plating begins to crack and split!', 12, 0, 100, 0, 2, 32332, 'say 1 damaged');
INSERT INTO `creature_text` VALUES (62164, 4, 0, 'Garalon becomes [Enraged] and prepares to execute a [Massive Crush]!', 12, 0, 100, 0, 2, 32333, 'say 1 berserk');
INSERT INTO `creature_text` VALUES (62397, 0, 0, 'Your defiance of the empress ends here!', 14, 0, 100, 0, 3, 30335, 'On Intro boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 1, 0, 'All of Pandaria will fall beneath the Wings of the Empress!', 14, 0, 100, 0, 3, 30336, 'On aggro boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 2, 0, 'The Empress commands it!', 14, 0, 100, 0, 3, 30338, 'On say slay boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 3, 0, 'Empress... I have... failed you...', 14, 0, 100, 0, 3, 30337, 'On death boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 4, 0, 'My blade never misses its mark!', 14, 0, 100, 0, 3, 30340, 'On whirling blades boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 5, 0, 'The skies belong to the Empress!', 14, 0, 100, 0, 3, 30341, 'On rain of blades boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 6, 0, 'Pitiful.', 14, 0, 100, 0, 3, 30339, 'On watchful eye boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 7, 0, 'To die for the empress is an honor! The wings of the empress cannot fail! You will pay for your transgressions!', 14, 0, 100, 0, 5, 30345, 'On group dies boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 8, 0, 'The Kor\'thik have no equal in combat!', 14, 0, 100, 0, 3, 30342, 'On summon reinforce boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 9, 0, 'Wind Lord Mel\'jarak begins to cast [Whirling Blades]!', 12, 0, 100, 0, 0, 3, 'On whirling blades boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 10, 0, 'Wind Lord Mel\'jarak begins to cast [Rain of Blades]!', 12, 0, 100, 0, 0, 3, 'On rain of blades boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62397, 11, 0, 'Wind Lord Mel\'jarak calls for reinforcements!', 12, 0, 100, 0, 0, 3, 'On reinforcements boss Wind Lord Mel\'jarak');
INSERT INTO `creature_text` VALUES (62511, 0, 0, 'You must be powerful, to have come this far. Yes, yes... you will make worthy test subjects.', 14, 0, 100, 0, 6, 31555, 'On EarthQuake boss Amber-Shaper Un\'sok');
INSERT INTO `creature_text` VALUES (62511, 1, 0, 'Watch your step!', 14, 0, 100, 0, 3, 31563, 'On EarthQuake boss Amber-Shaper Un\'sok');
INSERT INTO `creature_text` VALUES (62511, 2, 0, 'Feel fortunate to receive this gift.', 14, 0, 100, 0, 3, 31562, 'On OverRun boss Amber-Shaper Un\'sok');
INSERT INTO `creature_text` VALUES (62511, 3, 0, 'Is that all?', 14, 0, 100, 0, 3, 31560, 'Boss Amber-Shaper Un\'sok when slay');
INSERT INTO `creature_text` VALUES (62511, 4, 0, 'Forgive me, Empress....', 14, 0, 100, 0, 3, 31556, 'Boss Amber-Shaper Un\'sok when dies');
