INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(38225, '0', '0', 'Ya were foolish to come \'ere, Sea Witch. Ya escaped our vengeance once, but the Darkspear Tribe will not abide ya trespassin\' again.', '14', '0', '100', '0', '0', '0', ''),
(38225, '1', '0', 'I been waitin\' a long time for a chance to avenge my father. A great weight has been lifted from my shoulders.', '12', '0', '100', '0', '0', '0', ''),
(38225, '2', '0', 'I must be returnin\' ta Darkspear Hold. Please meet me there once Vanira is done with her healin\' of the boy.', '12', '0', '100', '0', '0', '0', ''),
(38225, '3', '0', 'I\'ve no love for Garrosh, dat much is sure, but leavin\' tha Horde is not a decision I be takin\' lightly.', '12', '0', '100', '0', '0', '0', ''),
(38225, '4', '0', 'It be done. Our ancient enemy is defeated.', '12', '0', '100', '0', '0', '0', '');
