INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (808, 0, 0, 'First da fire, and now da $r! You gonna die!', 12, 0, 0, 0, 0, 0, 'Grik\'nir the Cold');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (946, 0, 0, 'I gonna make you into mojo!', 12, 0, 0, 0, 0, 0, 'Frostmane Novice');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (946, 0, 1, 'Killing you be easy.', 12, 0, 0, 0, 0, 0, 'Frostmane Novice');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (946, 0, 2, 'My weapon be thirsty!', 12, 0, 0, 0, 0, 0, 'Frostmane Novice');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (946, 0, 3, 'You be dead soon!', 12, 0, 0, 0, 0, 0, 'Frostmane Novice');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 0, 'C\'mon, Fluffy! I just want to give you a bath!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 1, 'Carrots every day for a year if you stop!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 2, 'Come back, Fluffy!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 3, 'Don\'t make me get my pappy to catch you! He likes to catch bunnies using his rifle!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 4, 'Fluffy! Fluffy, stop running!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (1263, 0, 5, 'Why do you run from me, Fluffy?!', 12, 6, 100, 0, 0, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (7357, 0, 0, 'We will enslave the quilboar!', 12, 0, 100, 1, 0, 5819, 'Mordresh Fire Eye - SAY_OOC_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (7357, 1, 0, 'We will spread across this barren land!', 12, 0, 100, 1, 0, 5820, 'Mordresh Fire Eye - SAY_OOC_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (7357, 2, 0, 'Soon, the Scourge will rule the world!', 12, 0, 100, 22, 0, 5821, 'Mordresh Fire Eye - SAY_OOC_3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (7357, 3, 0, 'Slay them, my brethren! For the Scourge!', 14, 0, 100, 0, 0, 5822, 'Mordresh Fire Eye - SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 0, 0, 'All right, stay close.  These fiends will jump right out of the shadows at you if you let your guard down.', 12, 0, 100, 0, 0, 0, 'Belnistrasz SAY_QUEST_ACCEPTED');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 1, 0, 'Okay, here we go. It\'s going to take about five minutes to shut this thing down through the ritual. Once I start, keep the vermin off of me or it will be the end of us all!', 12, 0, 100, 0, 0, 0, 'Belnistrasz SAY_EVENT_START');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 2, 0, 'Three minutes left -- I can feel the energy starting to build! Keep up the solid defense!', 14, 0, 100, 0, 0, 0, 'Belnistrasz SAY_EVENT_THREE_MIN_LEFT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 3, 0, 'Just two minutes to go!  We\'re half way there, but don\'t let your guard down!', 14, 0, 100, 0, 0, 0, 'Belnistrasz SAY_EVENT_TWO_MIN_LEFT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 4, 0, 'One more minute!  Hold on now, the ritual is about to take hold!', 14, 0, 100, 0, 0, 0, 'Belnistrasz SAY_EVENT_ONE_MIN_LEFT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 5, 0, 'That\'s it -- we made it!  The ritual is set in motion, and idol fires are about to go out for good!  You truly are the heroes I thought you would be!', 14, 0, 100, 4, 0, 0, 'Belnistrasz SAY_EVENT_END');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 6, 0, 'You\'ll rue the day you crossed me, $N', 12, 0, 100, 0, 0, 0, 'Belnistrasz SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (8516, 7, 0, 'Watch out for the $N!', 12, 0, 100, 0, 0, 0, 'Belnistrasz SAY_WATCH_OUT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (10445, 0, 0, 'Greetings!', 12, 0, 100, 0, 0, 0, 'Selina Dourman');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (10445, 0, 1, 'Prepare for adventure!', 12, 0, 100, 0, 0, 0, 'Selina Dourman');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (10445, 0, 2, 'Welcome!', 12, 0, 100, 0, 0, 0, 'Selina Dourman');
