INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (37808, 1, 0, 'Corpse-men take Koroth\'s banner! Corpse-men get smashed to bitses!!!', 14, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (37953, 1, 0, 'Such easy prey.  Sylvanas will be most pleased!', 12, 0, 100, 0, 0, 0, 'Dark Scout');
