INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38029, 1, 0, 'Keep them occupied, my brothers!  Allow $n to retrieve the scythe!', 14, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38029, 2, 0, 'Fall back!  Retreat into the woods!', 14, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38051, 1, 0, 'Darius!  The Dark Rangers have the Scythe!  They got to it before we were able to reach it.', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38051, 2, 0, 'It will be done!', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38331, 1, 0, 'Gorerot crush puny worgen!!', 14, 0, 100, 0, 0, 23530, 'Gorerot to Krennan Aranas');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38415, 1, 0, 'He\'s too strong!  Use the catapults to bring him down!', 14, 0, 100, 0, 0, 19499, 'Lord Darius Crowley');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38415, 2, 0, 'Let us join your father\'s force\'s, Liam. They\'ll need our help against Sylvanas.', 14, 0, 100, 0, 0, 19500, 'Lord Darius Crowley');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38426, 1, 0, 'The villagers were thankful to have Emberstone back.  They brought us a little something to help against the Forsaken!', 12, 0, 100, 0, 0, 19684, 'Lorna Crowley to Krennan Aranas');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38507, 1, 0, 'Let us go at once.  We will track Sylvanas down.  For Gilneas!', 12, 0, 100, 15, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38507, 2, 0, 'Follow Tobias Mistmantle into the Cathedral District.', 42, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38507, 3, 0, 'Let\'s do our best not to be seen, $n.', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38507, 4, 0, 'Did you hear that, $n?  Sylvanas is headed to the cathedral.  Let\'s hurry!', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38507, 5, 0, 'Quick!  Hide in the water!', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38530, 1, 0, 'You can assure Garrosh that this is a minor setback.  Our victory in Gilneas will be absolute.', 12, 0, 100, 1, 0, 20492, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38530, 2, 0, 'You\'d do well to watch your tone, General.  Neither you nor Garrosh have anything to worry about.  We\'ve ceased all production of the Plague, as he ordered.  We\'d never deploy it without his permission.', 12, 0, 100, 0, 0, 20493, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38530, 3, 0, 'Go with honor, General.', 12, 0, 100, 0, 0, 20494, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38530, 4, 0, 'What kind of question is that?  Of course we\'re deploying the Plague as planned!  Let the Gilneans enjoy their small victory.  Not even their bones will remain by tomorrow.', 12, 0, 100, 0, 0, 20495, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38533, 1, 0, 'It appears you\'re losing control of Gilneas, Sylvanas.  Garrosh fears he\'s going to have to carry out this invasion himself.', 12, 0, 100, 1, 0, 22620, 'General Warhowl');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38533, 2, 0, 'You sound very confident, your Majesty.  I seriously hope you do not plan to use the Plague.  Garrosh has explicitly forbidden it.', 12, 0, 100, 274, 0, 22621, 'General Warhowl');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38533, 3, 0, 'I will deliver my report to our leader, then.  By your leave, my lady.', 12, 0, 100, 66, 0, 22622, 'General Warhowl');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38537, 1, 0, 'My Lady!  Should I order my men to stop the deployment of the Plague?  Or are we to continue as planned?', 12, 0, 100, 66, 0, 20917, 'High Executor Crenshaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38537, 2, 0, 'As you wish!', 12, 0, 100, 66, 0, 20918, 'High Executor Crenshaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38766, 1, 0, 'Crowley!  You and your elven allies are hereby ordered to serve along the king\'s army.  Cursed or not, you are still bound by Gilnean law!', 12, 0, 100, 25, 0, 19636, 'Lord Godfrey');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38766, 2, 0, 'Impossible!', 14, 0, 100, 0, 0, 19637, 'Lord Godfrey');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38767, 1, 0, 'No, old friend.  I\'ve come to you as an equal.', 12, 0, 100, 0, 0, 19723, 'King Genn Greymane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38767, 2, 0, 'It is decided, then.  We will unite all Gilneans and drive the Forsaken from our lands.', 12, 0, 100, 0, 0, 19724, 'King Genn Greymane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (38768, 1, 0, 'Father!', 14, 0, 100, 0, 0, 19697, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (39933, 0, 0, 'Madness. Chaos. Much as I thrill to see the titans\' work undone, I do wish to remain alive when the master returns. I will help you.', 12, 0, 100, 0, 0, 0, 'Tyrus Blackhorn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40066, 0, 0, 'Wailing Weed emits a shrill cry!', 16, 0, 100, 0, 0, 0, 'Wailing Weed');
