INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40107, 1, 0, 'Others will come... the age of mortals... has passed.', 12, 0, 100, 0, 0, 0, 'Thol\'embaar');
