INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40148, 0, 1, 'Get away from him until the fire dies down!', 14, 0, 100, 0, 0, 0, 'Galrond of the Claw');
