INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 0, 0, 'The dark ashes swirl around Tyrus Blackhorn, who utters an incantation:', 16, 0, 100, 0, 0, 0, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 1, 0, 'Ashes from the plane below, scattered by the hand of foe...', 12, 0, 100, 0, 0, 20773, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 2, 0, 'Gods of terror, quake and thunder, tear these mortal bonds asunder!', 12, 0, 100, 0, 0, 20774, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 3, 0, 'Tyrus Blackhorn barks a cruel laugh.', 16, 0, 100, 0, 0, 20775, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 4, 0, 'Thank you, mortal! Your heroism in The Inferno has made my escape possible.', 12, 0, 100, 0, 0, 20776, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 5, 0, 'As a reward, I will allow you to live.', 12, 0, 100, 0, 0, 20777, 'Tyrus Blackhorn Freed');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40172, 6, 0, 'But your time will come soon enough. Yes, mortal. Soon enough...', 12, 0, 100, 0, 0, 20778, 'Tyrus Blackhorn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 0, 0, 'Wants to kill us does it? Wants to know our secrets...', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 1, 0, 'We must destroys it.', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 2, 0, 'We burns it, we burns! Bring fire, yes!', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 3, 0, 'Cindermaul calls forth creatures from elsewhere in the Firelands.', 16, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 4, 0, 'Come winds, whisper whisper... Blows it, blows it, blows it away!', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 5, 0, 'Cindermaul calls forth creatures from Skywall.', 16, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 6, 0, 'We ... crushes it! Must ... make crush. Hurry friends!', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 7, 0, 'Cindermaul calls forth creatures from the heart of Deepholm.', 16, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40844, 8, 0, 'It mustn\'t know... it mustn\'t learn... what\'s ours...', 12, 0, 100, 0, 0, 0, 'Cindermaul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 0, 0, 'Impurity! Your fragile flesh defiles my work.', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 0, 'Water! Here!?', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 1, 'I\'ll get you, mortal! And your little wolves, too!', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 2, 'How? What is this trickery?', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 3, 'You infuriate me, mortal!', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 4, 'Aiiieeee!', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 5, 'It ... burns!', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 1, 6, 'This <glub> is not over yet, mortal...', 12, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40845, 2, 0, 'Forgemaster Pyrendius howls in fury as he is doused by water!', 16, 0, 100, 0, 0, 0, 'Forgemaster Pyrendius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40991, 0, 0, 'Tell Captain Tharran that I\'ll be back in Kharanos as soon as I\'ve verified that the bot is working correctly.', 12, 0, 100, 66, 0, 0, 'Mountaineer Dunstan');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (40994, 0, 0, 'I can\'t wait to get out there and help in the fight against those trolls.', 12, 0, 100, 66, 0, 0, 'Mountaineer Lewin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (41056, 0, 0, 'We\'ve been fighting nonstop since the cataclysm. It\'ll be nice to get a rest, if a brief one.', 12, 0, 100, 66, 0, 0, 'Mountaineer Valgrum');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (41121, 0, 0, 'What\'s Joren doin\' out there, anyhow?', 12, 0, 0, 0, 0, 0, 'Jona Ironstock');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (41121, 0, 1, 'Let\'s keep this fire going, dwarves!', 12, 0, 0, 0, 0, 0, 'Jona Ironstock');
