INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (41121, 0, 3, 'Don\'t fret, we\'ll be outta here soon.', 12, 0, 0, 0, 0, 0, 'Jona Ironstock');
