INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42489, 0, 0, 'We can\'t afford to let our enemy, Thermaplugg, continue to tighten his grip on the rest of Gnomeregan.', 12, 0, 100, 1, 0, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42489, 1, 0, 'He has ordered his crony, Razlo Crushcog, to keep us on the defensive. Hinkles, what\'s the latest intelligence on Razlo and his men?', 12, 0, 100, 1, 0, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42489, 2, 0, 'We have to be in position to strike against Crushcog when the mountaineers arrive.', 12, 0, 100, 1, 0, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42489, 3, 0, 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next, Thermaplugg. You\'re next!', 12, 0, 100, 25, 0, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42490, 0, 0, 'A messenger reached us from Brewnall Village this morning. The dwarves are sending a squad of mountaineers to help us.', 12, 0, 100, 25, 0, 0, 'Elgin Clickspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42491, 0, 0, 'It\'s been hard for our scouts to get information on what Crushcog is planning.', 12, 0, 100, 1, 0, 0, 'Hinkles Fastblast');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42491, 1, 0, 'What we do know is that Crushcog is using an advanced mechano-tank prototype and his irradiated gnomes are trying to repair more at the old arsenal.', 12, 0, 100, 1, 0, 0, 'Hinkles Fastblast');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 0, 'Finally, someone who\'s not a trogg!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 1, 'I don\'t ever want to smell unwashed trogg again!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 2, 'I\'m free! I\'m really free!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 3, 'Thank you for breaking me out of here!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 4, 'Thanks. Now, let\'s blow up that cave!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42645, 0, 5, 'You have no idea how happy I am to see you!', 12, 0, 100, 0, 0, 0, 'Captured Demolitionist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42747, 0, 0, 'Twilight scum! You did this to us!', 14, 0, 100, 0, 0, 0, 'Slain Crew Member Spirit hostile');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42757, 0, 0, 'They poisoned our supplies... we had cultists onboard all along...', 12, 0, 100, 0, 0, 0, 'Slain Crew Member Spirit friendly');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42757, 0, 1, 'Everyone started dropping like flies... everyone who ate the rations.', 12, 0, 100, 0, 0, 0, 'Slain Crew Member Spirit friendly');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42757, 0, 2, 'They tricked us into ambushing the Horde gunship... but why?', 12, 0, 100, 0, 0, 0, 'Slain Crew Member Spirit friendly');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42757, 0, 3, 'I heard them praying to their dark gods as everything went black... the Twilight\'s Hammer did this!', 12, 0, 100, 0, 0, 0, 'Slain Crew Member Spirit friendly');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42839, 0, 0, 'You! How did you escape detection by my sentry-bots?', 12, 0, 100, 0, 0, 0, 'Razlo Crushcog');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42839, 1, 0, 'No matter! My guardians and I will make short work of you. To arms, men!', 12, 0, 100, 0, 0, 0, 'Razlo Crushcog');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42839, 2, 0, 'You will never defeat the true sons of Gnomeregan!', 12, 0, 100, 0, 0, 21245, 'Razlo Crushcog');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 0, 0, 'Mekgineer Thermaplugg refuses to acknowledge that his defeat is imminent! He has sent Razlo Crushcog to prevent us from rebuilding our beloved Gnomeregan!', 12, 0, 100, 0, 0, 20890, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 1, 0, 'But $n has thwarted his plans at every turn, and the dwarves of Ironforge stand with us.', 12, 0, 100, 0, 0, 20891, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 2, 0, 'Let\'s send him crawling back to his master in defeat!', 12, 0, 100, 0, 0, 20892, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 3, 0, 'We\'ve done it! We\'re victorious!', 12, 0, 100, 0, 0, 20893, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 4, 0, 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next Thermaplugg. You\'re next!', 12, 0, 100, 0, 0, 20894, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 5, 0, 'Razlo Crushcog is no more! The people of Ironforge and Gnomeregan speak with one voice this day. Hear us well, Thermaplugg. The day of your defeat approaches!', 12, 0, 100, 0, 0, 20889, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42849, 6, 0, 'Mekkatorque-Cannon!', 14, 0, 100, 0, 0, 17561, 'High Tinker Mekkatorque');
