INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42852, 0, 0, 'Aye, let\'s teach this addle-brained gnome a lesson!', 12, 0, 100, 0, 0, 0, 'Mountaineer Stonegrind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (42852, 1, 0, 'That\'ll teach you to mess with the might of Ironforge and Gnomeregan!', 12, 0, 100, 0, 0, 0, 'Mountaineer Stonegrind');
