INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (43950, 1, 0, 'I remember now... it\'s all your fault!', 12, 0, 100, 0, 0, 0, 'Lurking Worgen captured');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (43950, 2, 0, 'You brought the worgen to Duskwood! You led the Dark Riders to my farm, and hid while they murdered my family!', 12, 0, 100, 0, 0, 0, 'Lurking Worgen captured');
