INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49355, 12, 0, '$n, I\'d recognize that face anywhere... What... What have they done to you, $n?', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_RECOG_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49355, 37, 0, 'Listen to me, $n. You must fight against the Lich King\'s control. He is a monster that wants to see this world - our world - in ruin. Don\'t let him use you to accomplish his goals. You were once a hero and you can be again. Fight, damn you! Fight his control!', 12, 0, 100, 5, 0, 0, 'special_surprise SAY_EXEC_LISTEN_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49355, 56, 0, 'You don\'t remember me, $n? I lost count of the number of jobs you and I ran back in the day on Kezan. Then I picked up a real bad Kaja\'Cola habit and you saved me! It was you who took me to Kalimdor to join the Steamwheedle Cartel. You were the only good goblin I knew.', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_NOREM_10');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49355, 58, 0, 'Without you I would have died. YOU! The most noble worgen I ever knew. What have they done to you, $n? How could this have happened? Remember the worgen you once were, $gbrother:sister,! You were our savior! Fight this! ', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_THINK_12');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49355, 61, 0, 'There... There\'s no more time for me. I\'m done for. Finish me off, $n. Do it or they\'ll kill us both. $n... Remember Kezan, our beloved home. This world is worth saving!', 12, 0, 100, 18, 0, 0, 'special_surprise SAY_EXEC_TIME_11');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 10, 0, '$n?', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_NAME_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 54, 0, 'C... cant we work this out somehow? ', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_START_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 55, 0, 'Alright, j... just let me... let me st... stand up.', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_PROG_8');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 57, 0, 'You don\'t remember me? We were both servants of Arugal back in Silverpine Forest. We put up with his merciless torture for ages. It was you who saved me on that fateful night when we escaped Shadowfang Keep. ', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_NOREM_11');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 59, 0, 'That\'s how I ended up in the Argent Dawn, because I knew it\'s what you woulda done. How could this have happened to you? \nRemember the goblin you once were, $gbrother:sister,! You were my best friend! Fight this!', 12, 0, 100, 0, 0, 0, 'special_surprise SAY_EXEC_THINK_11');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 60, 0, 'Listen to me, $n. You must fight against the Lich King\'s control. He is a monster that wants to see this world - our world - in ruin. Don\'t let him use you to accomplish his goals. You were once a hero and can be again. Fight, damn you! Fight his control!', 12, 0, 100, 5, 0, 0, 'special_surprise SAY_EXEC_LISTEN_5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49356, 62, 0, 'There... There\'s no more time for me. I\'m done for. Finish me off, $n. Do it or they\'ll kill us both. $n... Remember Gilneas, our beloved home. This world is worth saving.', 12, 0, 100, 18, 0, 0, 'special_surprise SAY_EXEC_TIME_12');
