INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56159, 0, 0, 'Come child. We have one final journey to take together before your training is complete.', 12, 0, 100, 0, 0, 27797, '');
