INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56159, 2, 0, 'Of the many ways to prove your worth, I require the simplest of you now. I must know that you will fight for our people', 12, 0, 100, 1, 0, 27801, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56159, 3, 0, 'Defeat the Guardian of the Elders, and we may pass.', 12, 0, 100, 1, 0, 27802, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56159, 4, 0, 'You\'ve become strong indeed, child. This is good. You will need that strength soon.', 12, 0, 100, 1, 0, 27803, '');
