INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56541, 16, 0, '|TInterface\\Icons\\ability_parry.blp:20|tMaster Snowdrift enables his |cFFFF0000|Hspell:106454|h[Parry Stance]|h|r!', 41, 0, 100, 0, 0, 0, 'Master Snowdrift');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56541, 17, 0, 'Master Snowdrift vanishes to the shadows and creates duplicate illusions of himself! Strike the illusions to dispel them!', 41, 0, 100, 0, 0, 0, 'Master Snowdrift');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56541, 18, 0, 'Master Snowdrift attacks you with his hand to hand techniques!', 41, 0, 100, 0, 0, 0, 'Master Snowdrift');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56585, 0, 0, 'Such a sacred text will not be wasted on me. Thank you!', 12, 0, 100, 1, 0, 0, 'Pearlfin Aqualyte to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56591, 0, 0, 'Yes! Now I am ready for battle.', 12, 0, 100, 6, 0, 0, 'Pearlfin Aqualyte to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56592, 0, 0, 'This is just what I needed.', 12, 0, 100, 15, 0, 0, 'Pearlfin Aqualyte to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56592, 0, 1, 'Yes! Those hozen won\'t know what hit them.', 12, 0, 100, 15, 0, 0, 'Pearlfin Aqualyte to Player');
