INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56636, 2, 0, 'The gates are about to fall. Quickly! Slaughter these meddlers!', 14, 0, 0, 0, 0, 29076, 'boss_commander_rimok');
