INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 0, 0, '$n, where\'s Master Shang?', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 1, 0, 'Bah, let a pandaren hope, would you? I\'m going to miss the old man.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 2, 0, 'When am I not respectful? You hurt me, Aysa.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 3, 0, 'A thorn? And I left my tweezers at home.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 4, 0, 'How could such a thing cause pain to something so large?', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 5, 0, 'Are you seeing what I\'m seeing? Is that a boat?', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56660, 6, 0, 'And those aren\'t pandaren down there. They\'ve got no fur.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56661, 0, 0, 'Meet me up in the cave if you would, friend.', 12, 0, 100, 1, 0, 27397, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 0, 0, '...Ji, they were in the Wood of Staves. You know where Master Shang is now.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 1, 0, 'Ji, be respectful when we speak to Shen-zin Su.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 2, 0, 'I might if you embarrass us.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 3, 0, 'Shen-zin Su, we are the descendants of Liu Lang. We\'ve sensed your pain, and we want to help.', 14, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 4, 0, 'What ails you Shen-zin Su? What can we do?', 14, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 5, 0, 'Of course, Shen-zin Su! But your shell is large, and I do not know where this thorn could be.', 14, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 6, 0, 'We will find it, and we will remove it. You have our word!', 14, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 7, 0, 'We\'ll know soon enough.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 8, 0, 'It is a boat... a whole airship! That\'s a bigger thorn than I was expecting.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 9, 0, 'Someone has crashed into our island. Removing this thorn may be more complicated than we thought.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56662, 10, 0, 'We should let Elder Shaopai know and then plan our next move.', 12, 0, 100, 0, 0, 0, 'Raufen - The Suffering of Shen-zin Su');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 0, 0, 'For 3,000 years, we have passed the knowledge of our people down. Elder to youth. Master to student.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 1, 0, 'Every elder reaches the day where he must pass on and plant his stave with the staves of his ancestors. Today is the day when my staff joins these woods.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 2, 0, '$n, our people have lived the wholes of their lives on this great turtle, Shen-zin Su, but not in hundreds of years has anyone spoken to him.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 3, 0, 'Now Shen-zin Su is ill, and we are all in danger. With the help of the elements, you will break the silence. You will speak to him.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 4, 0, 'Aysa and Ji have retrieved the spirits and brought them here. You are to go with them, speak to the great Shen-zin Su, and do what must be done to save our people.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56686, 5, 0, 'You\'ve come far, my young student. I see within you a great hero. I leave the fate of this land to you.', 12, 0, 100, 0, 0, 0, 'Raufen - Passing Wisdom quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56690, 0, 0, 'Action speaks louder than intent, stranger. My warriors intend to seek retribution, but only practice will make it so. Now go away. We are busy.', 12, 0, 100, 0, 0, 0, 'Instructor Sharpfin to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56693, 0, 0, 'Ah, you must be the outsider. Sadly, I am making weapons for war. Once, not long ago, I made instruments for prayer and celebration. It is this war that makes everyone stand on edge.', 12, 0, 100, 0, 0, 0, 'Ot-Temmdo to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56717, 0, 0, 'Oh yeah!', 14, 0, 100, 0, 0, 0, 'Hoptallus Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56717, 1, 0, 'Gonna spins around!', 14, 0, 100, 0, 0, 0, 'Hoptallus Furlwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56717, 2, 0, 'Urp...eats too many carrots...', 14, 0, 100, 0, 0, 0, 'Hoptallus Carrot Breath');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56717, 3, 0, 'You have...turnip...for a head...', 14, 0, 100, 0, 0, 0, 'Hoptallus Death');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56717, 4, 0, 'Hoptallus lets out a loud screech! The Virmen are coming!', 41, 0, 100, 15, 0, 0, 'Hoptallus Screech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56719, 0, 0, 'I will not be caged again. These Shado-Pan could not stop me. Neither shall you!', 14, 0, 100, 0, 0, 28346, 'Sha of Violence');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56719, 1, 0, 'So long as violence lurks in your hearts... I... will... return...', 14, 0, 100, 0, 0, 28347, 'Sha of Violence');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56719, 2, 0, 'Another violent death...', 14, 0, 100, 0, 0, 28348, 'Sha of Violence');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56742, 0, 0, 'We\'ve got to light that signal fire. Without reinforcements, they\'ll destroy the gate!', 12, 0, 0, 100, 0, 29068, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56743, 0, 0, 'But the mantid have overrun the area. There\'s no clear path!', 12, 0, 0, 100, 0, 29071, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56745, 0, 0, 'Don`t worry! I can make it!', 12, 0, 0, 100, 0, 27692, 'Serpent\'s Spine Trainee');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (56747, 0, 0, 'Let me show you my power.', 12, 0, 100, 0, 0, 28317, 'Gu Cloudstrike');
