INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (59074, 9, 0, 'Look, \"Chen Stormstout,\" we all can\'t be heroes, running from our responsibilities, tromping around the Dread Wastes, saving the world. Some of us are \"Artists.\" ', 12, 0, 100, 14, 0, 32001, 'Uncle Gao to Player');
