INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 0, 0, 'Ji may kill Shen-zin Su if we let him go through with this! Come with me, $n, quickly!', 12, 0, 100, 0, 0, 27412, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 1, 0, 'Ji, stop! This is reckless and stupid.', 12, 0, 100, 0, 0, 27413, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 2, 0, 'If you stop this, then maybe we can come up with one.', 12, 0, 100, 0, 0, 27414, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 3, 0, 'Doing THIS risks everything!', 12, 0, 100, 0, 0, 27415, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 4, 0, 'Ji... if this is what you must do, I won\'t stop you, but you\'re giving up everything for this. I hope you\'re right.', 12, 0, 100, 0, 0, 27416, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60729, 5, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60741, 1, 0, 'Aysa, this is our only option. We blow the ship free, then we heal his wound. We have no other solution.', 12, 0, 100, 0, 0, 27329, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60741, 2, 0, 'So we just wait until we think of something? Shen-zin Su is dying! Doing nothing risks everything!', 12, 0, 100, 0, 0, 27330, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60741, 3, 0, 'I\'d rather die knowing that we at least tried.', 12, 0, 100, 0, 0, 27331, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60763, 0, 0, 'Have you seen the yaungol fighting each other? It seems their troubles run deeper than we thought.', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60763, 0, 1, 'I knew someone would come for us! Let me help you fight the yaungol.', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60763, 0, 2, 'I\'m in your debt, stranger. Please let me help you.', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60763, 0, 3, 'Just what I was waiting for! Time to introduce those yaungol to my fists!', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60763, 0, 4, 'Thank you for freeing me, $gbrother:sister. I cannot let you fight alone.', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
