INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 0, 0, 'I\'ve never performed this ritual. I don\'t know what will happen, exactly.', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 1, 0, 'It\'s been passed down from ancient times, but none of our people have needed to use it since then.', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 2, 0, 'Let us see if this is sufficient to cleanse the corruption from my people\'s souls.', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 3, 0, 'It worked! It wo-', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 4, 0, 'Wh... what is happening? Why is the ritual affecting her?', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
