INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 6, 0, 'A beast emerged from you when I began the ritual. $n slew it!', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60973, 7, 0, 'That... thing must be what was controlling the villagers! I now know what we must do.', 12, 0, 100, 0, 0, 0, 'Raufen - The Ritual quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (60999, 0, 0, 'Your lights will go out. The darkness will envelop you. And you will fear the shadows that move within it.', 14, 0, 100, 0, 0, 28402, 'Sha of Fear - SAY_INTRO');
