INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61492, 0, 0, 'Thanks, friend. That was some firefight, but I knew that blue would pull through!', 12, 0, 100, 0, 0, 0, 'Wounded Pilot to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61492, 0, 1, 'Thank you. Time to lick my wounds and get back to the fight!', 12, 0, 100, 0, 0, 0, 'Critically Wounded Pilot to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61492, 0, 2, 'Nice seeing another Alliance face! I thought I was going to die out here.', 12, 0, 100, 0, 0, 0, 'Terribly Wounded Pilot to Player');
