INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61566, 0, 0, 'Gorai lives? I will go find him. Thank you, friend!', 12, 0, 100, 0, 0, 0, 'Raufen - Free the Dissenters quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61566, 0, 1, 'Thank you, friend... I\'ll make my way to the south!', 12, 0, 100, 0, 0, 0, 'Raufen - Free the Dissenters quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61566, 0, 2, 'You are very brave... thank you!', 12, 0, 100, 0, 0, 0, 'Raufen - Free the Dissenters quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 0, 0, 'I hope they make it this far. Then my strength and skill will be clear to all!', 14, 0, 100, 0, 0, 29392, 'Vizier Jin\'bak - SAY_INTRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 1, 0, 'Ah-hah! The lot of you will soon wish you hadn\'t come this way...', 14, 0, 100, 0, 0, 29390, 'Vizier Jin\'bak - SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 2, 0, 'But... I was... who will...?!', 14, 0, 100, 0, 0, 29391, 'Vizier Jin\'bak - SAY_SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 3, 0, 'Did you think you stood a chance?!', 14, 0, 100, 0, 0, 29394, 'Vizier Jin\'bak - SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 4, 0, 'Allow me to show you the power of amber alchemy...', 14, 0, 100, 0, 0, 29395, 'Vizier Jin\'bak - SAY_DETONATE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61567, 5, 0, '|cFFFE9A2ESap Globules|r begin to sprout from the tree!', 41, 0, 100, 0, 0, 0, 'Vizier Jin\'bak - EMOTE_GLOBULE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 0, 0, 'Thanks for the save, but we\'re still stuck in the middle of the entire mantid army.', 12, 0, 100, 11, 0, 31505, 'Yang Ironclaw - SAY_INTRO_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 1, 0, 'Look out there. Too many for us to take head on.', 12, 0, 100, 25, 0, 31506, 'Yang Ironclaw - SAY_INTRO_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 2, 0, 'Hmmm... I have a crazy idea that just might work, but we\'ll need your help heroes!', 12, 0, 100, 0, 0, 31507, 'Yang Ironclaw - SAY_INTRO_3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 3, 0, 'Let\'s give these mantid a taste of their own medicine!', 12, 0, 100, 0, 0, 31508, 'Yang Ironclaw - SAY_INTRO_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 4, 0, 'Li and Lo, get the sap into position!', 12, 0, 100, 0, 0, 31509, 'Yang Ironclaw - SAY_AGGRO_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 5, 0, 'Let\'s get this party started, shall we?', 12, 0, 100, 0, 0, 31510, 'Yang Ironclaw - SAY_AGGRO_2');
