INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 7, 0, 'We\'ll squish you like bugs!', 14, 0, 100, 5, 0, 31514, 'Yang Ironclaw - SAY_AGGRO_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 8, 0, 'We\'ve got your sap -- come and get it!', 14, 0, 100, 5, 0, 31515, 'Yang Ironclaw - SAY_AGGRO_5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 9, 0, 'A wave of |cFF00dc29Sik\'thik Swarmers|r joins the battle.', 41, 0, 100, 0, 0, 0, 'Yang Ironclaw - EMOTE_WAVE_ONE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 10, 0, 'Swarmers are coming up the ramp -- use the sap to slow them down!', 14, 0, 100, 0, 0, 31511, 'Yang Ironclaw - SAY_WAVE_ONE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 11, 0, 'A wave of |cFFB600B8Sik\'thik Demolishers|r joins the battle.', 41, 0, 100, 0, 0, 0, 'Yang Ironclaw - EMOTE_WAVE_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 12, 0, 'Demolishers are carrying explosives! Any direct hit will blow them up!', 14, 0, 100, 0, 0, 31504, 'Yang Ironclaw - SAY_WAVE_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 13, 0, 'A wave of |cFF00dc29Sik\'thik Swarmers|r and a |cFF815d18Sik\'thik Warrior|r joins the battle.', 41, 0, 100, 0, 0, 0, 'Yang Ironclaw - EMOTE_WAVE_THREE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 14, 0, 'Warrior coming in!  Focus your fire!', 14, 0, 100, 0, 0, 31512, 'Yang Ironclaw - SAY_WAVE_THREE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 15, 0, 'A wave of |cFFB600B8Sik\'thik Demolishers|r and a |cFF815d18Sik\'thik Warrior|r joins the battle.', 41, 0, 100, 0, 0, 0, 'Yang Ironclaw - EMOTE_WAVE_FOUR');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 16, 0, '|TInterfaceIconsinv_misc_blackironbomb.blp:20|t The |cFFdd7400Sik\'thik Amberwing|r moves into position to |cFFFF0000|Hspell:120559|h[Bombard]|h|r the platform!', 41, 0, 100, 0, 0, 0, 'Yang Ironclaw - EMOTE_BOMBARD');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 17, 0, 'The Amberwing is flying in, watch out!', 14, 0, 100, 0, 0, 31503, 'Yang Ironclaw - SAY_AMBERWING');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 18, 0, 'Well, that was fun, wasn\'t it?', 12, 0, 100, 5, 0, 31497, 'Yang Ironclaw - SAY_OUTRO_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 19, 0, 'Li and Lo, go look after our friends down below.', 12, 0, 100, 0, 0, 31498, 'Yang Ironclaw - SAY_OUTRO_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 20, 0, 'Ah, this gate is locked, but I think I have something that\'ll open it.', 12, 0, 100, 0, 0, 31499, 'Yang Ironclaw - SAY_GATE_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 21, 0, 'Let me see here... thirty second fuse...', 12, 0, 100, 0, 0, 31500, 'Yang Ironclaw - SAY_GATE_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 22, 0, 'Whoops, did I say thirty? I meant three.', 12, 0, 100, 0, 0, 31501, 'Yang Ironclaw - SAY_GATE_3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61620, 23, 0, 'Go, hurry! You must stop the siege!', 12, 0, 100, 0, 0, 31502, 'Yang Ironclaw - SAY_GATE_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 0, 0, 'Fools! Attacking the might of the mantid head on?  Your deaths will be swift.', 14, 0, 100, 0, 0, 30264, 'Commander Vo\'jak - SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 1, 0, 'Swarmers, attack!  Tear the flesh from their bones!', 14, 0, 100, 0, 0, 30265, 'Commander Vo\'jak - SAY_WAVE_ONE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 2, 0, 'Demolishers, get in there!  Leave nothing but a pile of ash!', 14, 0, 100, 0, 0, 30266, 'Commander Vo\'jak - SAY_WAVE_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 3, 0, 'Warriors, trample these weaklings!  Crush them!', 14, 0, 100, 0, 0, 30267, 'Commander Vo\'jak - SAY_WAVE_THREE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 4, 0, 'Everyone! Send everything! Slaughter these cowards, they are making us look like fools!', 14, 0, 100, 0, 0, 30268, 'Commander Vo\'jak - SAY_WAVE_FOUR');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 5, 0, 'Where... where is it? Worthless, useless... damn you!', 14, 0, 100, 0, 0, 30272, 'Commander Vo\'jak - SAY_AIR_SUPPORT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 6, 0, 'Pah! Those who failed deserved to die; you have merely culled the weak!', 14, 0, 100, 0, 0, 30262, 'Commander Vo\'jak - SAY_ENGAGE_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 7, 0, 'I must finish this myself, then. So be it!', 14, 0, 100, 0, 0, 30263, 'Commander Vo\'jak - SAY_ENGAGE_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 8, 0, 'Carpet bomb the platform! Send in the Amberwing!', 14, 0, 100, 0, 0, 30269, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 8, 1, 'They are holding us off, bring in the air support!', 14, 0, 100, 0, 0, 30270, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 8, 2, 'Amberwing, get in there! Lay siege to the holdouts!', 14, 0, 100, 0, 0, 30271, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 9, 0, 'It was... a trap...', 14, 0, 100, 0, 0, 30273, 'Commander Vo\'jak - SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 10, 0, 'Too easy!', 14, 0, 100, 0, 0, 30274, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 10, 1, 'Were you even trying?!', 14, 0, 100, 0, 0, 30275, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61634, 10, 2, 'Haha, you die so easily!', 14, 0, 100, 0, 0, 30276, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 0, 0, 'Walk with me. All three of you.', 12, 0, 100, 0, 0, 29574, 'Raufen - The Alliance Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 1, 0, 'I understand you want to join the Alliance.', 12, 0, 100, 0, 0, 29575, 'Raufen - The Alliance Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 2, 0, 'We always have need of allies. I\'d be a fool to turn you away, especially at a time of war.', 12, 0, 100, 0, 0, 29576, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 3, 0, 'But there are a number of things I need to make clear to you.', 12, 0, 100, 0, 0, 29577, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 4, 0, 'First: the races of the Alliance look out for one another.', 12, 0, 100, 0, 0, 29578, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 5, 0, 'You are expected to provide aid to Alliance members in need, whether they be human, gnome, draenei... or pandaren like yourselves.', 12, 0, 100, 0, 0, 29579, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 6, 0, 'It is a simple guideline, but a meaningful one. Do you understand?', 12, 0, 100, 0, 0, 29580, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 7, 0, 'Good. Because that brings me to my second point.', 12, 0, 100, 0, 0, 29581, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 8, 0, 'Not all pandaren have chosen the same path as you three.', 12, 0, 100, 0, 0, 29582, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 9, 0, 'Some have sided with the Horde.', 12, 0, 100, 0, 0, 29583, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 10, 0, 'They, along with the other barbarian clans of the Horde, are your new enemies.', 12, 0, 100, 0, 0, 29584, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 11, 0, 'Those who you once considered friends, or even those you might have loved, are now your sworn adversaries.', 12, 0, 100, 0, 0, 29585, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 12, 0, 'I am deeply sorry, but the battle lines have been drawn. I will not tolerate any fraternizing with the enemy, as you could expose our Alliance to danger.', 12, 0, 100, 0, 0, 29586, 'Raufen - The Allianec Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 13, 0, 'Do I make myself clear?', 12, 0, 100, 0, 0, 29587, 'Raufen - The Alliance Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 14, 0, 'Very well. We are nearly finished, $n.', 12, 0, 100, 0, 0, 29588, 'Raufen - The Alliance Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 15, 0, 'Few people know this, but I was once a pit fighter myself. A gladiator for the Horde\'s amusement. So I know a thing or two about martial combat.', 12, 0, 100, 0, 0, 29568, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 16, 0, 'Now, pandaren... let me see what you\'ve got!', 12, 0, 100, 0, 0, 29569, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 17, 0, 'Don\'t hold back now! Let\'s have it!', 12, 0, 100, 0, 0, 29570, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 18, 0, 'What\'s the matter? You\'ll have to do better than that.', 12, 0, 100, 0, 0, 29571, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 19, 0, 'Come on, pandaren! HIT ME!', 14, 0, 100, 0, 0, 29572, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (61796, 20, 0, 'Ha ha ha haaa! I haven\'t felt like this in YEARS! Don\'t worry, $n... I\'ve been in worse scraps than this. A fighting spirit like yours is welcome in the Alliance. Now, let\'s get back to business.', 12, 0, 100, 0, 0, 29573, 'Raufen - An Old Pit Fighter');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 0, 0, 'You\'ve made a wise decision to join my Horde.', 12, 0, 100, 0, 0, 30746, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 1, 0, 'There are some that would call us barbarians... the \"mongrel races\" of Azeroth.', 12, 0, 100, 0, 0, 30747, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 2, 0, 'They are ignorant, and blind to reality. Look around you, pandaren.', 12, 0, 100, 0, 0, 30748, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 3, 0, 'You will find no better partner in battle than an orc.', 12, 0, 100, 0, 0, 30749, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 4, 0, 'The tauren have made themselves useful. Look - this one is a shaman.', 12, 0, 100, 0, 0, 30750, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 5, 0, 'Even a blood elf can hold a sword.', 12, 0, 100, 0, 0, 30751, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 6, 0, 'Do you see it? Everyone in my Horde earns their keep. You and your friends will be no exception.', 12, 0, 100, 0, 0, 30752, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 7, 0, 'Do you understand this, pandaren?', 12, 0, 100, 0, 0, 30753, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 8, 0, 'Hm. Good.', 12, 0, 100, 0, 0, 30754, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 9, 0, 'Moving on. I know that you are not the only pandaren to escape from that island.', 12, 0, 100, 0, 0, 30755, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 10, 0, 'Some of your kind chose to join the Alliance.', 12, 0, 100, 0, 0, 30756, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 11, 0, 'They are now your enemies.', 12, 0, 100, 0, 0, 30757, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 12, 0, 'You may have had friends and kin who chose to cast their lot with the enemies of the Horde. They are no longer your friends... no longer your family.', 12, 0, 100, 0, 0, 30758, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 13, 0, 'The minute they put on that Alliance tabard, they died. I will NOT tolerate any lingering ties across enemy lines. Traitors to the Horde will die a traitor\'s death!', 12, 0, 100, 0, 0, 30759, 'Raufen - The Horde Way');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 14, 0, 'Do I make myself clear?', 12, 0, 100, 0, 0, 30760, 'Raufen - The Horde Way');
