INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62087, 16, 0, 'Meet me in the Valley of Honor. I have a gift for you.', 12, 0, 100, 0, 0, 30762, 'Raufen - The Horde Way');
