INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62171, 0, 0, 'Priestess Flameheart has betrayed us! The temple is lost!', 14, 0, 100, 0, 0, 0, 'Serpent Warrior');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62171, 0, 1, 'The Sha are too many, we must flee while we still have the chance!', 14, 0, 100, 0, 0, 0, 'Serpent Warrior');
