INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 0, 0, 'Ah yes. One does not forget the taste of amber.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 1, 0, 'Back when the world was still young, the first kypari trees bloomed.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 2, 0, 'From the trees, the amber flowed and nurtured us. Kyparite, we called it, the lifeblood of the earth.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 3, 0, 'It healed our wounds and made us bold. It tasted like sweetness and fire, and gave us strength to conquer all the lesser races.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 4, 0, 'Many were the threats to our race after the usurpers remade the world. Narrow was the path ahead.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 5, 0, 'It was I who suggested our greatest warriors be cast in amber to sleep, to be awakened at the swarm\'s greatest times of need.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 6, 0, 'It was the sleep from which one could never awake. A great risk. A great sacrifice. But necessary.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 7, 0, 'Thus, I was the first Paragon. This is why, I am the Prime.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (62180, 8, 0, 'And now, the Klaxxi call us to battle. Now, I am awakened.', 12, 0, 100, 0, 0, 0, 'Raufen - Living Amber quest talk');
