INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64705, 3, 0, 'You will not deceive us. We see you for who you truly are!', 12, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64813, 0, 0, 'I see the paragon has been awakened.', 12, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64813, 1, 0, 'The paragon does not speak truth, Wakener. His mind did not survive encasement.', 12, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64813, 2, 0, 'He cannot allow him to live in this madness. The Klaxxi will want him put down.', 12, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64813, 3, 0, 'You and the Klaxxi are fools! Your arrogance will be your end!', 12, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (64813, 4, 0, 'Empress She\'zeer knows your plans. Your treachery will be punished...', 14, 0, 100, 0, 0, 0, 'Raufen - Corruption Runs Deep');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 0, 'I have never seen a trainee with skills such as yours. I must tell the others.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 1, 'My skills are no match for yours. I admit defeat.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 2, 'Thank you for reminding me that I must train more diligently.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 3, 'That was a good match. Thank you.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 4, 'You are an honorable opponent.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 5, 'You fight honorably, friend.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 6, 'You fought well. I must learn more from you in the future.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65471, 0, 7, 'Your skills are too great. I yield.', 12, 0, 100, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65586, 0, 0, 'Thank you. I owe you greatly.', 12, 0, 100, 0, 0, 0, 'Raufen - On Spawn - Talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (65586, 1, 0, 'I overheard a great deal through the cage. Rensai will be pleased with what I have to tell him.', 12, 0, 100, 0, 0, 0, 'Raufen - On Spawn - Talk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 0, 0, 'Wakener, your deeds have earned you the trust of the Klaxxi.', 12, 0, 100, 1, 0, 30312, 'Kil\'ruk the Wind-Reaver - Say 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 1, 0, 'You are to be rewarded. Walk with me.', 12, 0, 100, 1, 0, 30313, 'Kil\'ruk the Wind-Reaver - Say 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 2, 0, 'We mantid are an elder race. The pandaren you associate with - they are but children. They have their role to play.', 12, 0, 100, 1, 0, 30314, 'Kil\'ruk the Wind-Reaver - Say 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 3, 0, 'Each cycle, our young swarm their walls. The pandaren slay the weak. The strong return.', 12, 0, 100, 1, 0, 30315, 'Kil\'ruk the Wind-Reaver - Say 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 4, 0, 'With each generation, we grow ever stronger.', 12, 0, 100, 1, 0, 30316, 'Kil\'ruk the Wind-Reaver - Say 5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 5, 0, 'Before your history began, our empire was vast. We shared this world with our sister kingdoms, Ahn\'Qiraj and Azjol-Nerub.', 12, 0, 100, 1, 0, 30317, 'Kil\'ruk the Wind-Reaver - Say 6');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 6, 0, 'Our Gods were many, and powerful. We mantid worshipped the seven heads of Y\'Shaarj.', 12, 0, 100, 1, 0, 30318, 'Kil\'ruk the Wind-Reaver - Say 7');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 7, 0, 'Great was the Old One, and terrible was His wrath. He consumed hope and begat despair; He inhaled courage and breathed fear.', 12, 0, 100, 1, 0, 30319, 'Kil\'ruk the Wind-Reaver - Say 8');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 8, 0, 'When the usurpers came - the ones you call \"Titans\" - Y\'Shaarj was destroyed.', 12, 0, 100, 1, 0, 30320, 'Kil\'ruk the Wind-Reaver - Say 9');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 9, 0, 'His last terrible breath has haunted this land ever since, but the shadows he left behind are mere whispers of his former glory.', 12, 0, 100, 1, 0, 30321, 'Kil\'ruk the Wind-Reaver - Say 10');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 10, 0, 'I tell you now, because you have earned this warning.', 12, 0, 100, 1, 0, 30322, 'Kil\'ruk the Wind-Reaver - Say 11');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 11, 0, 'Your gods are not your gods, outsider.', 12, 0, 100, 1, 0, 30323, 'Kil\'ruk the Wind-Reaver - Say 12');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 12, 0, 'If the Old Ones ever return, we mantid will once again stand by their side.', 12, 0, 100, 1, 0, 30324, 'Kil\'ruk the Wind-Reaver - Say 13');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 13, 0, 'The wisest among you will do the same.', 12, 0, 100, 1, 0, 30325, 'Kil\'ruk the Wind-Reaver - Say 14');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (66800, 14, 0, 'That is all that needs to be said. Come, take your reward, and remember what you have learned today.', 12, 0, 100, 1, 0, 30326, 'Kil\'ruk the Wind-Reaver - Say 15');
