INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(138460, 'frFR', 'C\’est beaucoup trop cher.', '', 26365),
(138459, 'frFR', 'Je ne peux me le permettre.', '', 26365),
(138458, 'frFR', 'Mes fonds sont insuffisants.', '', 26365),
(138457, 'frFR', 'Ceci n\’est pas un sac.', '', 26365),
(138456, 'frFR', 'Je manque de souffrance.', '', 26365),
(138455, 'frFR', 'Je n\’ai pas assez de souffrance.', '', 26365),
(138454, 'frFR', 'Je manque de fureur.', '', 26365),
(138453, 'frFR', 'Je n\’ai pas assez de fureur.', '', 26365),
(138452, 'frFR', 'Cet objet est en cours de recharge.', '', 26365),
(138447, 'frFR', 'Les Dieux très anciens ? Faites-moi rire ! Certains ont la bouche à la place des yeux et d\’autres les yeux à la place de la bouche. Du grand n\’importe quoi !', '', 26365),
(138446, 'frFR', 'Oui, je manipule l\’énergie du Vide. Ma faim est insatiable… Mais je me refrène pour garder la ligne.', '', 26365),
(138445, 'frFR', 'Alleria est ma préférée des sœurs Coursevent. Plus fougueuse que Vereesa, mais moins sanguinaire que la morte-vivante.', '', 26365),
(138444, 'frFR', 'Coïncidence ou pas, le violet a toujours été ma couleur préférée. Même avant que je rejoigne le Vide.', '', 26365),
(138443, 'frFR', 'On peut penser ce qu\’on veut de la doctrine du Vide mais au moins, ça, c\’est une culture !', '', 26365),
(138442, 'frFR', 'Si vous cherchiez quelqu\’un de grand, ténébreux et tourmenté, vous l\’avez trouvé ! Surtout ténébreux et tourmenté en fait…', '', 26365),
(138441, 'frFR', 'Je sais ce que vous pensez : « Super, encore des Elfes… » Mais je suis certain que vous ne vous attendiez pas à des Elfes du VIDE, avouez-le !', '', 26365),
(138440, 'frFR', 'Compris.', '', 26365),
