UPDATE `broadcast_text_locale` SET `Text1_lang`='Bonjour ? Qui est là ? Je vous entends !', `VerifiedBuild`=26365 WHERE (`ID`=138712 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Est-ce que quelqu\’un m\’entend ?', `VerifiedBuild`=26365 WHERE (`ID`=138711 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Maman ? Maman ! Pourquoi il fait tout noir ? J\’ai peur…', `VerifiedBuild`=26365 WHERE (`ID`=138708 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le Vindicaar est un véritable miracle de la Lumière, un symbole de ce que furent les érédars… et de ce que sont encore les Draeneï. Sachez néanmoins que toutes les cabines ne sont pas aussi fastueuses que celle-ci. Certaines sont même franchement étroites.', `VerifiedBuild`=26365 WHERE (`ID`=137870 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Il s\’est écoulé un temps considérable depuis notre départ, même pour un Elfe. Bien des choses ont dû changer sur Azeroth.', `VerifiedBuild`=26365 WHERE (`ID`=137864 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quel vaisseau remarquable ! On dirait qu\’il se maintient dans les airs grâce aux cristaux dont les Draeneï sont si friands. Peut-être pourrions-nous également les utiliser pour emmagasiner de l\’énergie arcanique ?', `VerifiedBuild`=26365 WHERE (`ID`=137848 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Voyez, $c ! Un vaisseau propulsé par la Lumière et une armée de puissants soldats dont la force et l\’espérance de vie n\’ont rien à envier à celles des Elfes. Voilà de précieux renforts pour cette croisade.', `VerifiedBuild`=26365 WHERE (`ID`=137801 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Draeneï sancteforge combattent la Légion depuis des générations. J\’apprendrai peut-être une chose ou deux à leur contact.$b$bSait-on jamais…', `VerifiedBuild`=26365 WHERE (`ID`=137798 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='De sombres jours nous attendent… Assez parlé, voyagez jusqu\’en Silithus et constatez la blessure de notre monde de vos propres yeux.', `VerifiedBuild`=26365 WHERE (`ID`=137345 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La Lumière et l\’Ombre ne sont que de simples moyens. En ce qui concerne cet ennemi, libre à vous de choisir l\’arme qui vous plaît.', `VerifiedBuild`=26365 WHERE (`ID`=137217 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La Lumière et l\’Ombre ne sont que de simples moyens. En ce qui concerne cet ennemi, libre à vous de choisir l\’arme qui vous plaît.', `VerifiedBuild`=26365 WHERE (`ID`=137176 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Dommage qu\’il n\’en ait pas profité pour tuer l\’Elfe et son acolyte.', `VerifiedBuild`=26365 WHERE (`ID`=136582 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Chers Draeneï… Champions d\’Azeroth… Le moment est venu d\’envahir Argus.', `VerifiedBuild`=26365 WHERE (`ID`=135823 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je n\’ai jamais vu les Draeneï mobiliser une armée de cette ampleur.', `VerifiedBuild`=26365 WHERE (`ID`=135806 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un groupe de soldats sancteforge est piégé derrière les lignes ennemies. Nous devons les sauver.', `VerifiedBuild`=26365 WHERE (`ID`=135803 AND `locale`='frFR');
