UPDATE `broadcast_text_locale` SET `Text_lang`='Vous avez fait alliance avec ces traîtres de Draeneï et leur Lumière, et pourtant vous êtes toujours aussi faibles.', `Text1_lang`='Vous avez fait alliance avec ces traîtres de Draeneï et leur Lumière, et pourtant vous êtes toujours aussi faibles.', `VerifiedBuild`=26365 WHERE (`ID`=135257 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sancteforge, repliez-vous !', `VerifiedBuild`=26365 WHERE (`ID`=134691 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sancteforge, sécurisez cette position !', `VerifiedBuild`=26365 WHERE (`ID`=134680 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je suis occupée, $R. Que voulez-vous ?', `VerifiedBuild`=26365 WHERE (`ID`=134342 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le prophète Velen sera ravi de voir que vous avez répondu à son appel. Les Draeneï attendent ce jour depuis longtemps.', `VerifiedBuild`=26365 WHERE (`ID`=133222 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Votre aura est des plus étranges, $R. Votre présence a quelque chose de… surnaturel, comme si vous n\’aviez pas votre place ici.\n\n<Kalecgos baisse la tête et regarde Chromie.>.\n\nAh, je vois. Vous voyagez dans le temps. Cela… cela pourrait expliquer ce phénomène.\n\n<Kalecgos vous observe avec méfiance.>', `VerifiedBuild`=26365 WHERE (`ID`=132086 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Allez, les Pandarens, c\’est à vous !', `VerifiedBuild`=26365 WHERE (`ID`=131047 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’adore regarder les Pandarens se déhancher. Ça va balancer !', `VerifiedBuild`=26365 WHERE (`ID`=131046 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Quelle chorégraphie ! On n\’a jamais rien vu de pareil sur Azeroth ! Faites du bruit pour les Draeneï !', `VerifiedBuild`=26365 WHERE (`ID`=131043 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Accueillons chaleureusement les Draeneï !', `VerifiedBuild`=26365 WHERE (`ID`=131042 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ces Gnomes ne sont-ils pas a-do-rables ?', `VerifiedBuild`=26365 WHERE (`ID`=131041 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Faisons monter les Gnomes sur scène afin que tout le monde puisse les voir !', `VerifiedBuild`=26365 WHERE (`ID`=131040 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Admirez la plastique et l\’assurance des Elfes de la nuit ! C\’est moi, ou il fait chaud tout à coup ?', `VerifiedBuild`=26365 WHERE (`ID`=131038 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\’est aux Nains de nous montrer comment ils font la fête !', `VerifiedBuild`=26365 WHERE (`ID`=131037 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='À en croire la rumeur, les Humains ne savent pas danser. Prouvez-moi le contraire !', `VerifiedBuild`=26365 WHERE (`ID`=131035 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\’est votre ville. Montrez-nous de quoi les Humains sont capables !', `VerifiedBuild`=26365 WHERE (`ID`=131034 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\’est notre ville, les Orcs. Montrez-leur de quoi on est capables !', `VerifiedBuild`=26365 WHERE (`ID`=130780 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Allez, les Pandarens, c\’est à vous !', `VerifiedBuild`=26365 WHERE (`ID`=130778 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’adore regarder les Pandarens se déhancher. Ça va balancer !', `VerifiedBuild`=26365 WHERE (`ID`=130777 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Au tour des Elfes de sang d\’investir la piste de danse !', `VerifiedBuild`=26365 WHERE (`ID`=130776 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Admirez la magnifique chorégraphie des Elfes de sang !', `VerifiedBuild`=26365 WHERE (`ID`=130775 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Allez, les Taurens ! Montrez-nous comment on danse à Mulgore.', `VerifiedBuild`=26365 WHERE (`ID`=130772 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Gare à vos orteils ! C\’est aux Taurens d\’investir la piste et ils ont le sabot particulièrement lourd.', `VerifiedBuild`=26365 WHERE (`ID`=130771 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Allez, les Trolls ! Montrez à tout le monde que vous avez la danse dans la peau !', `VerifiedBuild`=26365 WHERE (`ID`=130769 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qui allez-vous représenter ? Theramore ? Le Kirin Tor ? Parlez à Nellie si vous avez besoin d\’un tabard !', `VerifiedBuild`=26365 WHERE (`ID`=130477 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mettez-nous des étoiles plein les yeux en revêtant vos tenues de sorcier ! Vous allez nous enchanter !', `VerifiedBuild`=26365 WHERE (`ID`=130475 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le style des Pandarens respire l\’harmonie. Montrez-nous votre paix intérieure !', `VerifiedBuild`=26365 WHERE (`ID`=130474 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faites-nous trembler avec vos plus effroyables costumes. Oooh… J\’en ai déjà la chair de poule !', `VerifiedBuild`=26365 WHERE (`ID`=130471 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le thème de notre compétition du jour est… abracadabrant !', `VerifiedBuild`=26365 WHERE (`ID`=130460 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le thème de notre compétition du jour est… zen !', `VerifiedBuild`=26365 WHERE (`ID`=130459 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le thème de notre compétition du jour est… monstrueux !', `VerifiedBuild`=26365 WHERE (`ID`=130456 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='À moi la liberté ! Votre punition pour cette insulte marquera les mémoires, Elfe !', `VerifiedBuild`=26365 WHERE (`ID`=130331 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avant que vous ne posiez la question, je vous dis tout de suite que j\’ignore à quoi ressemble cette personne. Dans mon premier rêve, c\’était un Elfe de la nuit, dans le deuxième un Tauren et dans le troisième, à nouveau un Elfe de la nuit. Chaque fois, nous nous aventurons aux quatre coins d\’Azeroth et de Draenor pour affronter une ribambelle de démons, de dragons, de Dieux très anciens et même des murlocs !\n\nJe n\’avais encore jamais ressenti une telle complicité. Tout cela paraissait si réel… Au cours de mon dernier rêve, je lui ai donné rendez-vous ici.', `VerifiedBuild`=26365 WHERE (`ID`=130158 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a des siècles de cela, je me tenais à cet endroit précis quand j\’ai appelé les Draeneï à me rejoindre. C\’est ici que je chercherai les autres fragments.', `VerifiedBuild`=26365 WHERE (`ID`=130139 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Admirez le chef-d\’œuvre des Draeneï, j\’ai nommé l\’Exodar !', `VerifiedBuild`=26365 WHERE (`ID`=130109 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je suis Magatha Totem-Sinistre, dite la Vieille mégère, matriarche de ma tribu.$b$bBaine a eu beau me bannir des Pitons-du-Tonnerre, je n\’en demeure pas moins le chef dont les Taurens ont cruellement besoin. Ils restent là, les bras croisés, à attendre que l\’Alliance et la Légion les balaient comme feuilles mortes. Mais nous autres, nous ne nous laisserons pas faire sans réagir. Tous les moyens sont bons pour survivre.$b$bLa Légion a donné l\’assaut et les Totems-Sinistres ont besoin de moi. Comptez-vous m\’aider ou me faire obstacle ?', `VerifiedBuild`=26365 WHERE (`ID`=129873 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Elfes ont construit ce temple sur le site d\’une ancienne puissance, un caveau qui pourrait abriter la dépouille de mon ennemi. Mais avec l\’aide de son disciple, le titan noir menace de revenir en ce monde plus fort qu\’il ne l\’a jamais été !', `VerifiedBuild`=26365 WHERE (`ID`=129288 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai du nouveau pour vous, $n. Tout ce que vous avez appris sur votre métier jusqu\’ici n\’était que la base. Il n\’y avait aucun défi, aucun style, aucune grâce !$b$bÉvidemment, je ne suis pas qu\’un simple historien de la mode, je suis aussi un philosophe, jeune $R, et, je veux bien le reconnaître, une icône du style par ici.', `VerifiedBuild`=26365 WHERE (`ID`=129253 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’Exodar, vaisseau et demeure des Draeneï. Vous trouverez un portail pour l\’Exodar au temple de la Lune.', `Text1_lang`='L\’Exodar, vaisseau et demeure des Draeneï. Vous trouverez un portail pour l\’Exodar au temple de la Lune.', `VerifiedBuild`=26365 WHERE (`ID`=129222 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu veux dire être suspendu pour l\’éternité, emprisonné par une Gobeline écervelée ? C\’est sûr, je m\’éclate…', `VerifiedBuild`=26365 WHERE (`ID`=129184 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Encore une chose, si vous osez toucher à un cheveu de mon cher Tauren, vous aurez un SÉRIEUX problème…', `VerifiedBuild`=26365 WHERE (`ID`=127974 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Hé, vous là ! C\’est de vous que parlait le Tauren !', `VerifiedBuild`=26365 WHERE (`ID`=127971 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Elfes des temps anciens étaient imbus d\’eux-mêmes et vouaient un culte absolu à leurs esprits. Que voilà une délectable ignominie !', `VerifiedBuild`=26365 WHERE (`ID`=127966 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Cet endroit a toujours regorgé de puissance. C\’est ce qui a attiré Aegwynn ici, et les Elfes avant elle, et les Trolls avant eux. Et avant les Trolls, eh bien…', `VerifiedBuild`=26365 WHERE (`ID`=127949 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je pourchasserai les Trolls jusque dans leur caverne et je récupérerai la brune marteau-tempête !', `VerifiedBuild`=26365 WHERE (`ID`=127869 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avec votre permission, j\’irai droit au but.\n\nDepuis que les Orcs rochenoires m\’ont « expulsé » des steppes Ardentes, je poursuis dans la clandestinité mes recherches sur les destriers de l\’effroi de Xoroth. Au cours de l\’un de mes rituels, j\’ai réussi à capturer un curieux démon venu de l\’autre côté, un spécimen appartenant à l\’espèce dite des « langues-de-wyrm ».\n\nAprès plusieurs semaines d\’interrogatoire, il a fini par me révéler que le seigneur Hel\’Nurath travaille à la mise au point d\’une monture plus puissante à l\’usage des gangrechevaliers xorothiens.\n\nCette bête, il l\’a baptisée le destrier du courroux.', `VerifiedBuild`=26365 WHERE (`ID`=127630 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bon, je me permets de faire ce genre de blague parce que, de un, je suis un Nain, de deux, je suis un chasseur !', `VerifiedBuild`=26365 WHERE (`ID`=127347 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La partie est perdue d\’avance, $R. Mais si seule la mort peut vous faire entendre raison, alors soit.', `VerifiedBuild`=26365 WHERE (`ID`=127296 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La Légion a donné des gemmes aux pouvoirs insidieux aux Gangrenuit. Emparez-vous de ces colifichets et détruisez-les. Ces Elfes le paieront de leur vie.', `VerifiedBuild`=26365 WHERE (`ID`=127278 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mon bouclier a repoussé des armées entières. Je doute fortement qu\’$gun:une:r; simple $R comme vous puisse en venir à bout.', `VerifiedBuild`=26365 WHERE (`ID`=127264 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je vais vous secouer comme une poupée de chiffon, $R !', `VerifiedBuild`=26365 WHERE (`ID`=127239 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Mieux vaut les tuer ici plutôt que les voir piétiner mes récoltes. Allons-y, Elfe. ', `VerifiedBuild`=26365 WHERE (`ID`=126809 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai racheté ce ballon à un Gobelin minuscule. Il m\’a garanti qu\’il ferait un très bon affût pour la chasse à l\’élan ! ', `VerifiedBuild`=26365 WHERE (`ID`=126670 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En face, un Nain et son compagnon absolument loyal, qui ne ferait JAMAIS de mal à son maître. Absolument jamais.', `VerifiedBuild`=26365 WHERE (`ID`=126535 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='En face, un Nain et son compagnon absolument loyal, qui ne ferait JAMAIS de mal à son maître. Absolument jamais.', `VerifiedBuild`=26365 WHERE (`ID`=126532 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai vu le démon profiter du chaos ambiant pour filer par un portail. Il était grand comme deux Taurens, voire trois !', `VerifiedBuild`=26365 WHERE (`ID`=126494 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’espère que vous avez fait votre testament, $Rt ! J\’affame cet ours depuis une semaine. Il ne va faire qu\’une bouchée de vous, je vous le garantis !', `VerifiedBuild`=26365 WHERE (`ID`=126325 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En tant que Pandaren, cette fleur comestible ne peut pas vous être inconnue. C\’est l\’heure de passer à table.', `Text1_lang`='En tant que Pandarène, cette fleur comestible ne peut pas vous être inconnue. C\’est l\’heure de passer à table.', `VerifiedBuild`=26365 WHERE (`ID`=126179 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je sais à quel point sa présence ici est un outrage aux yeux des Taurens, Muln.', `VerifiedBuild`=26365 WHERE (`ID`=126157 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai le plaisir de vous présenter un Draeneï obsédé par les mascottes. Voici Sach\'katzuum !', `VerifiedBuild`=26365 WHERE (`ID`=126127 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’ai le plaisir de vous présenter un Draeneï obsédé par les mascottes. Voici Sach\'katzuum !', `VerifiedBuild`=26365 WHERE (`ID`=126102 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Beau travail, petit Gnome. Bientôt, il ne restera plus rien pour s\’opposer à la puissance de la Légion !', `VerifiedBuild`=26365 WHERE (`ID`=125420 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rien de tel qu\’un ennemi commun pour assurer la cohésion d\’une armée.$b$bIl y a quelques jours encore, ces Elfes s\’étripaient. Aujourd\’hui, grâce à vos efforts, nous sommes fin prêts à prendre le palais Sacrenuit.$b$bJ\’aurais aimé me battre en première ligne, mais Tyrande a insisté pour que vous et moi restions à l\’arrière-garde avec les Souffrenuit. Venez, voyons comment ces Elfes règlent leurs conflits.', `VerifiedBuild`=26365 WHERE (`ID`=125347 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Comme vous me l\’aviez demandé, je me suis renseignée sur la situation au bastion Gangrâme. Il semblerait que vos efforts aient nettement ralenti le trafic. Mais cela ne suffira pas à y mettre un terme définitif.$b$bLe bruit court qu\’une rafle de déserteurs se prépare suite aux évènements du port d\’Astravar. Prévenez vos hommes. D\’autres transfuges risquent de se tourner vers vous prochainement.$b$bDans un registre plus léger, une nouvelle mode fait rage au palais. Tous les courtisans arborent des couvre-chef à oiseaux orange. Cela défie l\’entendement.$b$bJe continue encore et toujours à rechercher notre ami commun. Je vous recontacterai dès que j\’en saurai plus.$b$bCourage, Première arcaniste, nous vaincrons.', `VerifiedBuild`=26365 WHERE (`ID`=125271 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Dame S\’theno est descendue prêter main-forte il y a quelques heures, mais la situation est grave. Devrions-nous la rejoindre ?', `VerifiedBuild`=26365 WHERE (`ID`=125199 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Elfes de sang sont des combattants aguerris et disciplinés. Leur soutien nous sera fort utile.', `VerifiedBuild`=26365 WHERE (`ID`=125133 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de la nuit utilisent des arcs ! Cela fait des siècles qu\’on ne fabrique plus d\’arc à Suramar. Je me demande s\’ils m\’en prêteraient un pour que je m\’entraîne...', `VerifiedBuild`=26365 WHERE (`ID`=125131 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand je pense que nous descendons tous des mêmes Elfes... Aucun d\’entre nous n\’est invulnérable aux outrages du temps, semble-t-il.', `VerifiedBuild`=26365 WHERE (`ID`=125130 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Puits de la nuit. Une source insondable d\’énergie arcanique. Que seraient devenus les Sin\’dorei si nous avions disposé d\’une telle manne de puissance ?$b$bPeu importe. Nous avons forgé notre propre destin. Notre gloire nous appartient et nous ne sommes plus esclaves de nos appétits.$b$bCe siège est une occasion unique, $n. Nous avons uni nos forces avec nos anciens frères, nos ennemis, pour lutter contre les mages les plus puissants au monde. De notre succès dépend l\’avenir des Elfes de tous horizons.', `VerifiedBuild`=26365 WHERE (`ID`=125125 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Peut-être vais-je écrire un livre consacré aux Elfes de la nuit. Leur histoire est véritablement fascinante.', `VerifiedBuild`=26365 WHERE (`ID`=125110 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La magie de ces Elfes de sang est absolument remarquable. Espérons que cela suffise à contrer la puissance du Puits de la nuit.', `VerifiedBuild`=26365 WHERE (`ID`=125095 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ma vie a tellement changé depuis que vous m\’avez apporté ce fruit.$b$bMon frère me manque cruellement, mais je comprends son sacrifice.$b$bJ\’ai quelque chose à vous demander. Je tiens à remercier les autres pour leur bonté, et j\’ai aussi besoin de venger la mort de mon frère.$b$bConduisez-moi auprès de la Première arcaniste, j\’aimerais lui prêter allégeance.', `VerifiedBuild`=26365 WHERE (`ID`=124831 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un Elfe de sang et son acolyte $R assis tranquillement à discuter stratégie... Quoi de plus naturel ?', `VerifiedBuild`=26365 WHERE (`ID`=124767 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La Légion est nombreuse, par ici. Essayez de ne pas attirer l\’attention sur vous, $R.', `VerifiedBuild`=26365 WHERE (`ID`=124764 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, $R ! Vous cherchez quelque chose ?', `VerifiedBuild`=26365 WHERE (`ID`=124712 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes sont nerveux. Le Kirin Tor parvient tout juste à maintenir la paix.', `VerifiedBuild`=26365 WHERE (`ID`=124652 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\’est le genre de coup bas auquel je m\’attendrais plutôt de la part d\’un Nain...', `VerifiedBuild`=26365 WHERE (`ID`=124491 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Maudits Elfes de la nuit !', `Text1_lang`='Maudits Elfes de la nuit !', `VerifiedBuild`=26365 WHERE (`ID`=124438 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes n\’arrêtent pas de se tirer dans les pattes. On essaie juste de limiter la casse, que voulez-vous.', `Text1_lang`='Les Elfes n\’arrêtent pas de se tirer dans les pattes. On essaie juste de limiter la casse, que voulez-vous.', `VerifiedBuild`=26365 WHERE (`ID`=124244 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Khadgar a lancé un appel à tous ceux disposés à envoyer des troupes pour épauler son armée. Et devinez qui a débarqué ?\n\nLes Elfes de sang et les Elfes de la nuit. Deux races aux rivalités si tenaces qu\’elles font pâlir la vieille querelle opposant l\’Alliance et la Horde.', `Text1_lang`='Khadgar a lancé un appel à tous ceux disposés à envoyer des troupes pour épauler son armée. Et devinez qui a débarqué ?\n\nLes Elfes de sang et les Elfes de la nuit. Deux races aux rivalités si tenaces qu\’elles font pâlir la vieille querelle opposant l\’Alliance et la Horde.', `VerifiedBuild`=26365 WHERE (`ID`=124243 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les tensions entre la Horde et l\’Alliance sont palpables quand leurs membres se retrouvent sur le champ de bataille. Mais entre les Elfes... l\’ambiance est carrément à couper au couteau.', `Text1_lang`='Les tensions entre la Horde et l\’Alliance sont palpables quand leurs membres se retrouvent sur le champ de bataille. Mais entre les Elfes... l\’ambiance est carrément à couper au couteau.', `VerifiedBuild`=26365 WHERE (`ID`=124238 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Par ici, $R. Essayez de ne pas alerter tous les gardes à la ronde en approchant.', `VerifiedBuild`=26365 WHERE (`ID`=124155 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Longue vie à la Première arcaniste !', `Text1_lang`='Longue vie à la Première arcaniste !', `VerifiedBuild`=26365 WHERE (`ID`=124027 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous appelez ça une posture de combat ? Un peu de sérieux, n\’importe quel Elfe dans ce camp pourrait vous tailler en pièces.', `VerifiedBuild`=26365 WHERE (`ID`=124012 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pauvre de moi ! Je suis cuit !\n\nSavez-vous qui est le Dr Dwenfer ? Était. Est peut-être encore. Allez savoir.\n\nLa mort n\’allait pas l\’arrêter, il avait tout prévu. Un Gnome brillant, comme tous les illuminés de son espèce.\n\nQuand son cœur a cessé de battre, les machines implantées dans son corps ont remplacé ses fluides corporels par de l\’humeur jaunâtre et l\’ont électrifié. J\’ignore s\’il s\’agit de non-mort, d\’ingénierie alchimique ou de quelque chose de... supérieur.\n\nDès qu\’on a su qu\’un poste d\’apprenti se libérait auprès du Dr Dwenfer, on s\’est précipités. Je ne sais plus si je suis mort ou vivant, et je suinte le fluide jaunâtre...\n\nLes autres se sont surnommés les « gnomes trimeurs ». Bonne chance avec ces abrutis.', `VerifiedBuild`=26365 WHERE (`ID`=123981 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Sin\’dorei sont bien placés pour comprendre le triste sort des Souffrenuit. Croyez bien que je compatis.$b$bMais Thalyssra doit se rendre à l\’évidence : guerre et insurrection sont deux choses bien différentes.$b$bJusqu\’à présent, ses rebelles s\’en sont tirés avec quelques escarmouches ponctuelles. Contrairement à eux, nous autres Elfes de sang sommes des combattants aguerris.', `VerifiedBuild`=26365 WHERE (`ID`=123561 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Suramar. Ma ville natale. Pas une seule fois en dix mille ans je n\’ai imaginé revenir un jour avec une armée pour l\’arracher aux Elfes dont je recherchais autrefois la protection.', `VerifiedBuild`=26365 WHERE (`ID`=123553 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Quand je vous ai confié les Larmes d\’Élune, je ne pensais pas vous revoir avant la bataille finale contre la Légion.$b$bJe ne m\’attendais pas à vous retrouver en compagnie d\’une bande de drogués au mana, aux portes de ma ville natale.$b$bParlez vite, $R. Je ne suis pas d\’humeur à écouter les suppliques de Thalyssra.', `VerifiedBuild`=26365 WHERE (`ID`=123552 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Parlez vite, $R. Je ne veux pas vous voir traîner dans mon camp.$b$bVotre Horde a laissé l\’un de mes amis pour mort sur le rivage Brisé. Une chance pour vous que nous ayons un ennemi commun pour l\’instant.', `VerifiedBuild`=26365 WHERE (`ID`=123551 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Mais Suramar se souvient ! Nous sommes les Sacrenuit, les derniers véritables Elfes. Vous autres n\’êtes que de pâles copies de notre grandeur.', `VerifiedBuild`=26365 WHERE (`ID`=123486 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Sin\’dorei ? De tous les Elfes, vous devriez comprendre que je fais tout pour préserver mon peuple. Mais non, vous prenez le parti de la racaille et des monstres.', `VerifiedBuild`=26365 WHERE (`ID`=123484 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Quel\’dorei ? Des paysans qui se piquent de noblesse mais mêlent leur sang à celui des races inférieures. Vous n\’êtes pas dignes du nom de Haut-Elfes.', `VerifiedBuild`=26365 WHERE (`ID`=123483 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Kaldorei ? Vous souillez votre glorieux passé en vous terrant parmi les arbres. Derrière vos airs de dévotion, vous n\’êtes plus que des sauvages, de vulgaires Trolls des forêts.', `VerifiedBuild`=26365 WHERE (`ID`=123482 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça alors, $R ! Est-ce le sceptre de Sargeras ? Mais oui ! C\’est un sacré bout d\’histoire que vous avez là, hein ?', `VerifiedBuild`=26365 WHERE (`ID`=123406 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Regarde ça, Broulf. $gCelui-ci:Celle-ci; tient la fureur des tempêtes dans ses mains. Ha ha, bats-toi bien, $gpetit:petite; $R !', `VerifiedBuild`=26365 WHERE (`ID`=123351 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Trouvez un soigneur pour Willy ! Je n\’ai jamais vu un Gnome aussi pâle depuis… heu, en fait tous les Gnomes sont pâles.', `VerifiedBuild`=26365 WHERE (`ID`=123267 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Qu\’est-ce que vous me voulez, $R ?', `VerifiedBuild`=26365 WHERE (`ID`=122847 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='[%s] se confesse : Je n\’arrive pas à croire que les armes des Taurens soient aussi imposantes.', `Text1_lang`='[%s] se confesse : Je n\’arrive pas à croire que les armes des Taurens soient aussi imposantes.', `VerifiedBuild`=26365 WHERE (`ID`=122775 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ah ça, il a été pris par surprise ! Va falloir exterminer ces démons une bonne fois pour toutes !', `VerifiedBuild`=26365 WHERE (`ID`=122418 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Misérable Gnome ! Ton arrogance te perdra !', `VerifiedBuild`=26365 WHERE (`ID`=122342 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Comment avez-vous pu en arriver là, $n ? Vous êtes plus un démon qu\’$gun:une:r; $R.', `VerifiedBuild`=26365 WHERE (`ID`=122089 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='De tous ses utilisateurs, ce sont les Draeneï qui en ont tiré le meilleur parti. Il faut espérer qu\’ils réussissent là où les autres ont échoué.', `VerifiedBuild`=26365 WHERE (`ID`=122079 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je me demande si les Elfes de la nuit pleureront votre mort.', `VerifiedBuild`=26365 WHERE (`ID`=121982 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je me demande si les Elfes de la nuit pleureront votre mort.', `VerifiedBuild`=26365 WHERE (`ID`=121914 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous êtes déjà $gsorti:sortie; avec $gune Taurène:un Tauren; ? Évitez.', `VerifiedBuild`=26365 WHERE (`ID`=121853 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La Horde n\’est pas si terrible. Les Gnomes, en revanche…', `VerifiedBuild`=26365 WHERE (`ID`=121851 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je me souviens d\’un temps où le monde était plus simple. Où le simple fait de chasser les kobolds de sa propre ville faisait de vous un héros.$b$bJe me souviens d\’un temps où on respectait les anciens, le temps d\’avant cet aventurier troll et ses habitudes de jeune… Troll.', `VerifiedBuild`=26365 WHERE (`ID`=121694 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les bâtiments des Orcs ne sont peut-être pas aussi confortables que les cryptes de Fossoyeuse, mais les rondins de bois voyagent bien mieux que les pierres décrépites.', `VerifiedBuild`=26365 WHERE (`ID`=121523 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je vous en prie, n\’appelez pas les gardes. Je suis venu pour venger Vol\’jin, pas pour causer des ennuis.$b$bCe Troll était mon ami. Ensemble, nous avons surmonté tant d\’épreuves… Je lui dois la vie.$b$bNous nous étions juré que si l\’un de nous mourait, l\’autre le vengerait. J\’ai toujours cru qu\’il me survivrait…$b$bQuoi qu\’il m\’en coûte, je tiendrai ma promesse.', `VerifiedBuild`=26365 WHERE (`ID`=121461 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Le chaman aveugle entend le bruit de vos pas et tend les mains pour toucher votre visage.>\n\nAlliance. Hum… Et $R de surcroît. <Il tousse.> Nous nous sommes peut-être croisés autrefois sur un champ de bataille.\n\nJ\’ai entendu l\’appel des éléments il y a bien longtemps de cela. En un autre lieu. Une autre époque. <Il tousse.> Je vois les esprits briller en vous… Je sens que le vent et les vagues se soumettent à votre volonté. Quelle puissance… Votre Alliance doit être fière de vous compter dans ses rangs.\n\nNous vivons des heures bien sombres, $R. Mais votre force donne un peu d\’espoir au vieil orc que je suis. Peut-être puis-je vous rendre la pareille… <Il tousse.> Pour peu que vous acceptiez l\’aide d\’un ancien ennemi…', `VerifiedBuild`=26365 WHERE (`ID`=121441 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ouais, ouais. Écoutez, si on devait se cacher de tous les monstres qui ont été des Elfes, on n\’aurait plus rien à chasser. Alors, du calme et ouvrez l\’œil. On n\’est pas seuls.', `VerifiedBuild`=26365 WHERE (`ID`=121279 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très mauvaise idée. Les démons, c\’est une chose, mais les satyres ? Ce sont des Elfes de la nuit qui ont succombé à la corruption.', `VerifiedBuild`=26365 WHERE (`ID`=121275 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Écoutez. Marius voit régulièrement des choses qui vous terrifieraient au point que vous finiriez par me ressembler.$b$bMais même lui n\’est pas à l\’aise dans cette grotte. Il y rôderait de mauvais rêves et des Elfes corrompus.$b$bBah ! Nous avons suivi un puissant satyre jusqu\’ici et il n\’est pas question de repartir sans avoir au moins tenté de l\’asservir.', `VerifiedBuild`=26365 WHERE (`ID`=121247 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est grâce à l\’aide des Taurens que ma chair est devenue bois, ma peau, feuilles, et ma sagesse, éternelle. Même si mon peuple l\’oublie, moi, je ne l\’oublierai jamais.', `VerifiedBuild`=26365 WHERE (`ID`=120636 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le Briseur d\’Elfes ? Quel horrible nom.', `VerifiedBuild`=26365 WHERE (`ID`=120411 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Jadis, les fal\’dorei furent des Elfes… Aujourd\’hui, ce sont des monstres, le produit des énergies chaotiques de l\’arcan\’dor défaillant. Ils doivent être éradiqués.', `VerifiedBuild`=26365 WHERE (`ID`=120371 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Voulez-vous vraiment partir ? partir ? Vous devriez rester pour finir l\’exercice, $R.', `Text1_lang`='Voulez-vous vraiment partir ? partir ? Vous devriez rester pour finir l\’exercice, $R.', `VerifiedBuild`=26365 WHERE (`ID`=120158 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si je ne m\’abuse, c\’était un Tauren… et je m\’abuse rarement.\n\nIl est venu jusqu\’ici avec un exemplaire de ce livre, tout comme vous aujourd\’hui, mais il était dans l\’incapacité de me prouver que le drake noir était mort, aussi ne lui ai-je rien dit. Il cherchait tout particulièrement à localiser Porte-Cendres.\n\nSi vous parvenez à le retrouver, peut-être pourra-t-il vous en apprendre davantage sur la question.', `VerifiedBuild`=26365 WHERE (`ID`=119676 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Restez sur vos gardes, $R. Les Sacrenuit se sont enhardis. Ils s\’aventurent au-delà des limites de leur ville.', `VerifiedBuild`=26365 WHERE (`ID`=119446 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Alors c\’est fait. Ne vous avisez plus jamais de faire appel à moi, $R.', `VerifiedBuild`=26365 WHERE (`ID`=119402 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Assassiner les Dryades pour en faire d\’ignobles créatures… J\’ignorais que la magie des chouettards pouvait être aussi vile ! Merci d\’y avoir mis fin.', `VerifiedBuild`=26365 WHERE (`ID`=119380 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les chouettards sont les gardiens de lieux sacrés mais, privés de la lumière d\’Élune, ils peuvent devenir agressifs. Sauvez les Dryades qu\’ils menacent !', `VerifiedBuild`=26365 WHERE (`ID`=119379 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hors de ma vue, $R. Je ne veux rien avoir à faire avec vos plans insensés.', `VerifiedBuild`=26365 WHERE (`ID`=119163 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Voilà qui est fort téméraire, $R. Les seigneurs élémentaires ne sont pas connus pour leur générosité.\n\nToujours est-il que si vous récupérez les deux liens et du minerai suffisamment puissant pour les transformer en quelque chose d\’utile, je m\’arrangerai pour vous obtenir une audience auprès du seigneur des Vents Tonneraan.\n\nUtilisez le temps dont vous disposez jusque-là pour réfléchir au bien-fondé de cette rencontre. Je comprendrais parfaitement que vous décidiez de ne pas revenir.', `VerifiedBuild`=26365 WHERE (`ID`=119158 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous détenez un puissant lien de la famille du seigneur du Feu. Ce n\’est pas un jouet, $R. De tels liens peuvent menacer les grands seigneurs élémentaires eux-mêmes. Il est effroyablement dangereux d\’en posséder ne serait-ce qu\’un seul.\n\nComptez-vous vous en servir pour emprisonner le seigneur des Vents, ou avez-vous d\’autres projets ?', `VerifiedBuild`=26365 WHERE (`ID`=119150 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Votre destinée est au cœur de ma perle, $R !', `VerifiedBuild`=26365 WHERE (`ID`=116453 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quelle impudence, Orc ! Vous auriez au moins pu m\’attendre !', `VerifiedBuild`=26365 WHERE (`ID`=116350 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Que signifie « atal\’ai » en zandali, la langue des Trolls ?', `VerifiedBuild`=26365 WHERE (`ID`=116260 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Comment s\’appelait l\’Humaine qui aida Thrall à s\’évader de sa prison au donjon de Fort-de-Durn ?', `VerifiedBuild`=26365 WHERE (`ID`=116197 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Au cours de quelle guerre les Elfes de la nuit et les vols draconiques ont-ils affronté les armées qiraji ?', `VerifiedBuild`=26365 WHERE (`ID`=116177 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Quel Orc provoqua l\’ouverture de nombreux portails sur Draenor et la destruction du monde des Orcs ?', `VerifiedBuild`=26365 WHERE (`ID`=116172 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Comment s\’appelait l\’Humain qui réduisit Thrall en esclavage au donjon de Fort-de-Durn ?', `VerifiedBuild`=26365 WHERE (`ID`=116167 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Quel Elfe de la nuit fut nommé geôlier d\’Illidan Hurlorage ?', `VerifiedBuild`=26365 WHERE (`ID`=116094 AND `locale`='frFR');
