UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Quel est le plus haut rang dans la hiérarchie des guetteuses Elfes de la nuit ?', `VerifiedBuild`=26365 WHERE (`ID`=116033 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai eu si peur ! Ces vilains Draeneï s\’en prennent à tout le monde !', `Text1_lang`='J\’ai eu si peur ! Ces vilains Draeneï s\’en prennent à tout le monde !', `VerifiedBuild`=26365 WHERE (`ID`=115998 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pourquoi les autres Draeneï veulent nous faire mal ?', `Text1_lang`='Pourquoi les autres Draeneï veulent nous faire mal ?', `VerifiedBuild`=26365 WHERE (`ID`=115995 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Jetez-vous sur la barrière du prophète, serviteurs ! Elle sera bientôt brisée, et avec elle l\’espoir des Draeneï. ', `VerifiedBuild`=26365 WHERE (`ID`=115929 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On dirait… un Nain ? Comment a-t-il pu survivre ?', `VerifiedBuild`=26365 WHERE (`ID`=115540 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous êtes bien bête, $R. Vous ne savez pas ce que vous ratez. C\’est délicieux !', `VerifiedBuild`=26365 WHERE (`ID`=115452 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Griffe-Serre est perdue depuis si longtemps que de nombreux Taurens ne l\’ont jamais vue. Les anciens racontent encore comment nos plus grands chasseurs ont jadis défendu notre terre armés de cette lance.', `VerifiedBuild`=26365 WHERE (`ID`=114944 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai une grosse clé à molette et un Gnome lépreux furax ! À votre place, j\’approcherais pas. Pigé, les démons ?', `VerifiedBuild`=26365 WHERE (`ID`=114859 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces traîtres de « Draeneï » sont lâches, jusque dans la mort ! Massacrez les corps pour que les vivants découvrent le sort qui les attend !', `VerifiedBuild`=26365 WHERE (`ID`=114844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Fais poing-glace, Humain !', `VerifiedBuild`=26365 WHERE (`ID`=114628 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Plusieurs siècles durant, notre ordre a caché et défendu un lieu secret, le tombeau de Tyr.\n\nTyr, un gardien des titans qui avait accompli de vaillants exploits pour le salut d\’Azeroth, fut vaincu par la traîtrise et la puissance des Dieux très anciens. Loken le félon envoya de puissants C\’Thraxxi l\’assassiner. Il parvint à les repousser, tuant l\’un d\’eux et forçant le second à s\’enfuir, mais cette victoire fut au prix de sa vie.\n\nCertains des Vrykuls qui l\’accompagnaient furent si émus par son sacrifice qu\’ils restèrent là pour veiller sur son tombeau. Ils furent par la suite remplacés par des Humains… nous. Pendant longtemps, nous avons réussi à cacher cet endroit. Jusqu\’à récemment.', `VerifiedBuild`=26365 WHERE (`ID`=114512 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Dire que, moi, le pauvre vieux Tehd de Lordaeron, je rends ces chasseurs de démons nerveux. Hé hé hé !$B$BN\’oubliez jamais qu\’au royaume des Elfes millénaires gangrimprégnés, un paysan doté d\’un bon sort de bannissement est roi.', `VerifiedBuild`=26365 WHERE (`ID`=114152 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les naaru évoquent-ils l\’éternel conflit ? Vous ont-ils dit que l\’histoire de votre monde n\’est qu\’un instant dans celle de la création ? Vous ont-ils parlé de ceux qui étaient là avant les Draeneï ? Non ?', `VerifiedBuild`=26365 WHERE (`ID`=113988 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Une autre brute au service de Sargeras qui prétend aider les Elfes. Ils ne voient rien venir.', `VerifiedBuild`=26365 WHERE (`ID`=113897 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Mmmm ! Les Taurens ici sont si… grands.', `VerifiedBuild`=26365 WHERE (`ID`=113847 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’érédar, ancien Draeneï tombé sous la coupe du seigneur démon Sargeras. Sa maîtrise de la magie de l\’Ombre et de la magie gangrenée vous forcera à adapter vos protections en conséquence. Souvent à la tête de contingents démoniaques, il représente à la fois une menace sérieuse et une cible de choix.', `VerifiedBuild`=26365 WHERE (`ID`=113668 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous serez la première à voir la Légion entrer dans ma ville, imbécile d\’Orque !', `VerifiedBuild`=26365 WHERE (`ID`=113576 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour Lune-d\’Argent !', `VerifiedBuild`=26365 WHERE (`ID`=113427 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Oh, il pleut des Elfes.', `VerifiedBuild`=26365 WHERE (`ID`=113075 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Oh, on a un petit… arrangement avec eux.\n\nOuais, ils veulent qu\’on emporte ces marchandises à Dalaran pour les revendre. C\’est vrai quoi, les clients se bousculent pas au portillon, par ici, hein ? En plus, nous les Gobelins, on a un don pour le commerce.\n\nY a pas à dire, ces Taurens ont du flair pour les affaires !', `VerifiedBuild`=26365 WHERE (`ID`=113025 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je parie que les Taurens seront de retour d\’une minute à l\’autre.\n\nEn attendant, qu\’est-ce que je peux faire pour vous, l\’$gami:amie; ?', `VerifiedBuild`=26365 WHERE (`ID`=113023 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Approchez, $R !', `VerifiedBuild`=26365 WHERE (`ID`=112951 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Oh, regardez ! Une ville entière de vilains petits Elfes !', `VerifiedBuild`=26365 WHERE (`ID`=112904 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes se cachent derrière leur barrière. Idiots ! C\’est le monde entier qui va s\’écrouler si la Légion triomphe !', `VerifiedBuild`=26365 WHERE (`ID`=112898 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ici, les Elfes se flétrissent par manque de magie. Ridicule ! Ils n\’avaient qu\’à voler celle des autres.', `VerifiedBuild`=26365 WHERE (`ID`=112897 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Elfes feraient tout pour échapper au Cauchemar. Les démons, eux, cherchent à en capter la puissance. Tous sont condamnés à échouer.', `VerifiedBuild`=26365 WHERE (`ID`=112884 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vu leur origine, les Taurens d\’ici sont incroyablement résistants à la corruption.', `VerifiedBuild`=26365 WHERE (`ID`=112881 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Elfes se terrent derrière leur mur. C\’est tout ce qu\’ils savent faire.', `VerifiedBuild`=26365 WHERE (`ID`=112857 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je n\’ai rien contre les boules de feu, mais j\’ai toujours préféré les armes blanches. Merci, $R. Ces dagues sont de pures merveilles.', `VerifiedBuild`=26365 WHERE (`ID`=112470 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Euh, il me semble bien que c\’est une Gobeline maintenant.', `VerifiedBuild`=26365 WHERE (`ID`=112459 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Félicitations, $R. Je suis impressionné. Les Régicides ont pris de nombreuses vies… Allez maintenant, c\’est votre tour d\’éliminer les faibles.', `VerifiedBuild`=26365 WHERE (`ID`=112379 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis le Nain qu\’il vous faut.', `VerifiedBuild`=26365 WHERE (`ID`=112136 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je ne suis pas la Dryade que vous recherchez.', `VerifiedBuild`=26365 WHERE (`ID`=112100 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Tisse-Rêves n\’ont que faire de ce qui vous différencie des autres mortels, $R. Ici, nous ne sommes que des druides. Ici, nous sommes tous unis au service d\’une même cause.', `VerifiedBuild`=26365 WHERE (`ID`=111824 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='<L\’image indistincte d\’une Gnome flotte devant vous, surgie d\’un lointain passé. Elle se concentre sur un puissant sort, mais vous confond avec quelqu\’un d\’autre lorsque vous approchez.>\n\nAlodi ! Vous êtes revenu. Montrez-moi donc votre bâton. Oui, oui… Bien sûr que je vais vous aider. Mais vous devriez entendre ce que le reste du conseil murmure dans votre dos. Vous avez de la chance qu\’ils aient besoin de vous.', `VerifiedBuild`=26365 WHERE (`ID`=111779 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Une ombre à moitié oubliée rôde à l\’arrière de la taverne, fouinant dans les parchemins qui s\’y trouvent. Réminiscence d\’un autre temps, le Haut-Elfe reconnaît votre bâton et vous salue d\’égal à égal.>\n\nVoici venue l\’heure de vérité. Vous êtes prêt, Alodi ? Vous êtes bien certain de pouvoir maîtriser une telle puissance ?', `VerifiedBuild`=26365 WHERE (`ID`=111776 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Le vague souvenir du Gnome vient effleurer votre conscience, telle une ombre d\’une époque lointaine. Son expression s\’illumine lorsqu\’il aperçoit le bâton que vous tenez entre les mains.>\n\nL\’heure est-elle venue, Alodi ? Votre bâton est-il prêt ?', `VerifiedBuild`=26365 WHERE (`ID`=111677 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Puis-je combattre à vos côtés, brave $R ?', `Text1_lang`='Puis-je combattre à vos côtés, brave $R ?', `VerifiedBuild`=26365 WHERE (`ID`=111385 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tiens, tiens, le grand retour du petit Elfe. Tu es du genre… tenace, dis-moi !', `VerifiedBuild`=26365 WHERE (`ID`=111300 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le seigneur des marées vous maudisse, $R !', `VerifiedBuild`=26365 WHERE (`ID`=111165 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Puits de soleil a été une source intarissable de puissance arcanique, mais c\’est désormais la Lumière sacrée qui coule en son sein… Ce qui laisse espérer que les Sin\’dorei, sous la férule de Lor\’themar Theron, puissent enfin être en mesure de surmonter leur addiction à la magie. Même si les Elfes de sang ont contribué aussi bien à la défaite de la Horde de Fer qu\’à la bataille contre Garrosh, leur avenir au sein de l\’Alliance n\’est plus aussi plausible qu\’il le fut à une époque. C\’est un futur incertain et instable qui attend les Elfes de sang, mais vous devez rester prêts à défendre les vestiges de votre civilisation et à racheter l\’âme entachée de votre peuple.', `VerifiedBuild`=26365 WHERE (`ID`=111163 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le commandement inspiré de la grande prêtresse Tyrande Murmevent a apporté prospérité et stabilité aux Elfes de la nuit. Aussi, avec l\’aide de l\’archidruide Malfurion Hurlorage, la nature a-t-elle retrouvé ses droits en Hyjal malgré la destruction causée par le Cataclysme. Le puissant Arbre-Monde Teldrassil, débarrassé de sa corruption, continue de guérir et de revigorer l\’esprit meurtri des Elfes de la nuit… et les anciens demi-dieux arpentent à nouveau le monde. Cependant, tout ne va pas pour le mieux sous les branches baignées de brume de Teldrassil. Les demi-dieux sortis de leur torpeur sentent que d\’anciens ennemis sont sur le point de se manifester. Aujourd\’hui, comme vos semblables l\’ont déjà fait d\’innombrables fois, vous devez vous tenir sur le pied de guerre pour éviter à Azeroth la destruction qui la guette.', `VerifiedBuild`=26365 WHERE (`ID`=111162 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Magni Barbe-de-Bronze, roi des Nains fidèle à son peuple, lui fut arraché quand une terrible malédiction le changea en statue de diamant au cœur de la capitale naine de Forgefer. Pour éviter une guerre civile faute de souverain, les Nains créèrent le conseil des Trois marteaux afin de répartir le pouvoir entre les clans rivaux. Depuis, cette coalition a tenté de maintenir une fragile stabilité…', `VerifiedBuild`=26365 WHERE (`ID`=111159 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La nation humaine de Hurlevent, à l\’avant-garde dans la campagne de l\’Alliance contre l\’impitoyable Horde de Fer, a une nouvelle fois regagné sa suprématie. Après avoir réussi à envahir Orgrimmar et à faire répondre de ses crimes le redoutable chef de guerre Garrosh Hurlenfer, le haut roi Varian Wrynn a juré qu\’Azeroth ne tomberait plus jamais aux mains des tyrans. Aujourd\’hui, alors que l\’horizon s\’obscurcit et que les tensions resurgissent entre les Humains et la Horde, leur ennemi éternel, l\’Alliance doit encore montrer l\’exemple à ceux qui désirent se battre pour le bien. La tempête approche, et face à elle, c\’est à vous d\’assurer l\’honneur de l\’humanité.', `VerifiedBuild`=26365 WHERE (`ID`=111157 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Exilés de leurs terres du cap Strangleronce il y a des années de cela, les Sombrelances ont tenté de se reconstruire une existence parmi les différentes races de la Horde. Cependant, les méfaits et la traîtrise du chef de guerre Garrosh Hurlenfer ont réduit à néant toute velléité d\’unité au sein de la Horde. Garrosh vaincu, les Trolls peuvent à nouveau croire à la promesse d\’un avenir plus serein sous la houlette avisée de Vol\’jin. Et même si de nouvelles menaces provenant de la tribu renégate des Zandalari pèse lourdement sur son esprit, Vol\’jin aspire au jour où tous les Trolls seront unis entre eux. Aujourd\’hui, c\’est à de courageux Trolls tels que vous de veiller à ce que l\’ambitieuse vision d\’unité de Vol\’jin devienne réalité.', `VerifiedBuild`=26365 WHERE (`ID`=111156 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Même si la défaite de l\’ancien chef de guerre Garrosh Hurlenfer présage d\’un avenir moins sombre pour les Taurens, les oripeaux du pouvoir pèsent lourdement sur les épaules de Baine Sabot-de-Sang. Il sait que les Taurens font partie intégrante de la Horde, mais il craint que le règne destructeur de Garrosh ait fait perdre à son peuple sa vision et sa raison d\’être. Aujourd\’hui, ce sont de courageux Taurens tels que vous qui doivent préserver les terres de leurs ancêtres et restaurer l\’honneur et la grandeur de la tribu… et de la Horde.', `VerifiedBuild`=26365 WHERE (`ID`=111154 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Après avoir mené campagne contre la sournoise Horde de Fer, les puissants Orcs de Durotar regardent maintenant vers l\’avenir. S\’efforçant de mettre derrière elle l\’héritage toxique de Garrosh Hurlenfer, son précédent chef de guerre, la Horde répond maintenant à un nouveau dirigeant : Vol\’jin, un Troll avisé et courageux. Bien que les exactions de Garrosh aient ravivé les tensions entre la Horde et son ennemi de toujours, l\’Alliance, c\’est à des Orcs tels que vous qu\’il revient d\’ouvrir la voie de la rédemption et de rétablir la gloire passée de la Horde.', `VerifiedBuild`=26365 WHERE (`ID`=111153 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Malfurion Hurlorage attendait impatiemment l\’arrivée de Huln et de ses Taurens. Krasus, aussi connu comme le dragon Korialstrasz, les soutenait depuis les cieux.', `VerifiedBuild`=26365 WHERE (`ID`=111147 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Parce que vous avez battu quelques Elfes ivres de magie, vous pensez pouvoir affronter un vrai fils de la Légion ?', `VerifiedBuild`=26365 WHERE (`ID`=111056 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='$R, venez-vous en $gami ou en ennemi:amie ou en ennemie; ? Si vous vous dites $gl\’allié:l\’alliée; de la forêt, prouvez-le. Aidez-moi à repousser la corruption qui menace mon foyer.', `VerifiedBuild`=26365 WHERE (`ID`=111044 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='« Est-ce un Gnome qui se dissimule sous vos cuissards ? Ou s\’agit-il plutôt d\’une paire de Gnomes ? »$b$bSire Morfléau trébucha, pris au dépourvu par l\’offensive si… directe de la princesse. Il rassembla ses esprits avant de répliquer…$b$b|cFF600000<Comment sire Morfléau pourrait-il réagir à présent ?>|r', `VerifiedBuild`=26365 WHERE (`ID`=110926 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='« C\’est là une bien grosse épée. Êtes-vous certain de savoir la manier ? »$b$bLe regard de sire Morfléau plongea vers ses braies, avant de recroiser celui de la Draeneï de sang royal. Imitant son demi-sourire, il reprit…$b$b|cFF600000<Que pourrait bien répliquer sire Morfléau à présent ?>|r', `VerifiedBuild`=26365 WHERE (`ID`=110925 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Dites-moi si vous voulez participer à la baston, $R.', `Text1_lang`='Dites-moi si vous voulez participer à la baston, $R.', `VerifiedBuild`=26365 WHERE (`ID`=110908 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Felo\’melorn ! L\’arme des rois. Dans les mains d\’Anasterian Haut-Soleil, la fureur de cette auguste lame a terrassé un millier de Trolls amani.', `VerifiedBuild`=26365 WHERE (`ID`=110288 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’imagine que vous avez entendu parler de la guerre des Anciens, n\’est-ce pas ? Eh bien, si on les écoute, les Taurens de Haut-Roc seraient responsables de la défaite de Tichondrius.\n\nMais ils ont reçu de l\’aide. Beaucoup d\’aide. Voulez-vous que je vous raconte cette histoire ?', `VerifiedBuild`=26365 WHERE (`ID`=109909 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et que suggères-tu, petit Orc ? Veux-tu que j\’affronte la Légion ardente seul ?', `VerifiedBuild`=26365 WHERE (`ID`=109430 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les forces de Haut-Roc, les Drogbars et même les Elfes seront balayés par les flammes de notre colère !', `VerifiedBuild`=26365 WHERE (`ID`=108955 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quant à la vulpille, elle pousse en abondance un peu partout dans Haut-Roc. Bonne chance, $R.', `VerifiedBuild`=26365 WHERE (`ID`=108553 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous sollicitez mon aide, mais vous n\’avez rien à offrir en retour, $R. Dans ce cas, vous pouvez peut-être rendre service à ma tribu.$b$bCela fait des années que nous n\’avons pas eu le plaisir de consommer du breuvage crin-de-rive. Leur remède est réputé pour ses vertus revigorantes et il est excellent pour la dentition. Par chance, il se trouve que j\’en connais la formule.$b$bJe vais vous confier mon mortier et mon pilon, ainsi que la recette. Rapportez-en suffisamment pour toute ma tribu et je réparerai votre flacon.$b$bC\’est d\’accord ?', `VerifiedBuild`=26365 WHERE (`ID`=108547 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En tant que Pandaren, vous pourriez avaler n\’importe quoi. Rien ne saurait détraquer votre estomac d\’acier. À table !', `Text1_lang`='En tant que Pandarène, vous pourriez avaler n\’importe quoi. Rien ne saurait détraquer votre estomac d\’acier. À table !', `VerifiedBuild`=26365 WHERE (`ID`=107968 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Qu\’est-ce que… Oh, non ! Mon père ! Il compte prendre possession de mon père !', `VerifiedBuild`=26365 WHERE (`ID`=107942 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne perdez rien pour attendre… mais vous n\’êtes pas encore digne d\’accueillir en votre sein mon formidable pouvoir. Non… pour l\’heure, j\’ai jeté mon dévolu sur votre… supérieur !', `VerifiedBuild`=26365 WHERE (`ID`=107941 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Jamais je ne vous servirai !', `VerifiedBuild`=26365 WHERE (`ID`=107940 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cela dit… votre lignée détient un grand pouvoir, fillette. Je vais me faire un plaisir de m\’en emparer.', `VerifiedBuild`=26365 WHERE (`ID`=107939 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vos ancêtres aussi étaient un peu trop sûrs d\’eux. Décidément, votre arrogance vous perdra.', `VerifiedBuild`=26365 WHERE (`ID`=107938 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Mon peuple a déjà vaincu les Ténèbres autrefois, et nous le referons aujourd\’hui.', `VerifiedBuild`=26365 WHERE (`ID`=107937 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous autres ronirs de Haut-Roc avez gagné en force en mon absence, mais ça ne suffira pas.', `VerifiedBuild`=26365 WHERE (`ID`=107936 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Montrez-vous, le Gris !', `VerifiedBuild`=26365 WHERE (`ID`=107935 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je reconnais que je ne faisais guère confiance aux représentants de votre race. Mais vous avez combattu avec témérité et vaillance. Allons jusqu\’au bout ensemble, voulez-vous ?\n\nVous sentez-vous de taille à vous rendre au cœur de ce chaos, $R ?', `VerifiedBuild`=26365 WHERE (`ID`=107930 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bas les armes, Elfes au visage pâle. Je suis ici pour $p.', `VerifiedBuild`=26365 WHERE (`ID`=107927 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous n\’avez pas pu sauver votre père de son vivant. Et l\’histoire se répète avec son esprit ! Quelle fille indigne.', `VerifiedBuild`=26365 WHERE (`ID`=107881 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Résistez-lui, père ! Ne le laissez pas prendre le contrôle !', `VerifiedBuild`=26365 WHERE (`ID`=107880 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ah, quelle ironie du sort… Vous ne vous attendiez sûrement pas à périr aux mains de votre propre père, malheureuse !', `VerifiedBuild`=26365 WHERE (`ID`=107879 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Entendez-vous les hurlements de l\’esprit d\’Ulan ? Ses sanglots ? Il sait qu\’il sera l\’instrument de votre destruction !', `VerifiedBuild`=26365 WHERE (`ID`=107878 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Rendez-moi mon père, monstre !', `VerifiedBuild`=26365 WHERE (`ID`=107876 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faites vite ! Je peux ouvrir un portail vers le monde des esprits, mais vous devez bannir les Ténèbres avant qu\’elles ne s\’approprient une nouvelle enveloppe.', `VerifiedBuild`=26365 WHERE (`ID`=107844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous arrivez trop tard, ma chère enfant. Désormais, le corps de votre père m\’appartient !', `VerifiedBuild`=26365 WHERE (`ID`=107839 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Acceptez les Ténèbres.', `VerifiedBuild`=26365 WHERE (`ID`=107822 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Ténèbres perceront le voile.', `VerifiedBuild`=26365 WHERE (`ID`=107817 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Déguerpissez, maudits esprits ! Dites à votre maître qu\’il n\’est pas le bienvenu ici !', `Text1_lang`='Déguerpissez, maudits esprits ! Dites à votre maître qu\’il n\’est pas le bienvenu ici !', `VerifiedBuild`=26365 WHERE (`ID`=107809 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je les ai vues ! Les Ténèbres approchent !', `Text1_lang`='Je les ai vues ! Les Ténèbres approchent !', `VerifiedBuild`=26365 WHERE (`ID`=107806 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Votre vie sera brève. Les Ténèbres l\’ont vu.', `VerifiedBuild`=26365 WHERE (`ID`=107772 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cette protection va nous aider à confiner un terrible fléau. Moi vivant, les Ténèbres ne connaîtront jamais la liberté !', `VerifiedBuild`=26365 WHERE (`ID`=107771 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, le Nain, la serrure, elle est coincée et j\’ai la caboche en feu. Si t\’es si malin, fais-le toi-même.', `VerifiedBuild`=26365 WHERE (`ID`=107668 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Non. Je viens des Pitons-du-Tonnerre. Je suis une aventurière, tout comme vous. Les Taurens de Haut-Roc me sont aussi étrangers que vous.\n\nMais je suis passionnée par l\’étude de Huln Haut-Roc, l\’un des plus grands Taurens qui aient jamais foulé ces terres, si ce n\’est LE plus grand. Et c\’est ici, à Haut-Roc, que j\’ai le plus de chances d\’en apprendre davantage à son sujet.', `VerifiedBuild`=26365 WHERE (`ID`=107616 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Le haut-examinateur vous toise d\’un air condescendant.>\n\nN\’avez-vous donc jamais entendu parler du Reliquaire, $R ?\n\nNotre quête est celle du pouvoir, bien sûr. Maintes armes prodigieuses inestimables sont enfouies dans ces terres. Les savoirs arcaniques oubliés des Bien-nés surpassent tout ce que vous et moi en connaîtrons jamais. Je cherche à retrouver les reliques perdues afin de mettre leur puissance au service de notre cause.\n\nÀ présent, cessez de m\’importuner avec vos questions.', `VerifiedBuild`=26365 WHERE (`ID`=107614 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Ténèbres. Elles ont débarqué sans crier gare et… je regrette, grand chef, mais elles ont emporté la dépouille de votre père au sommet.', `VerifiedBuild`=26365 WHERE (`ID`=107523 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Aile de mort lui-même avait conscience des forces maléfiques que les Ténèbres déchaîneraient si on les laissait agir impunément.$b$bC\’est un funeste jour pour nous tous.', `VerifiedBuild`=26365 WHERE (`ID`=107506 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous sentez-vous de taille à vous aventurer en plein cœur de ce chaos, $R ?', `VerifiedBuild`=26365 WHERE (`ID`=107433 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Faites vite, mes sœurs. Nous devons terminer le maléfice de façon à ce que personne, étrangers et Taurens confondus, ne puisse échapper à son emprise !', `VerifiedBuild`=26365 WHERE (`ID`=107314 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ha ! Essayez seulement de l\’atteindre, Elfe de la nuit !', `VerifiedBuild`=26365 WHERE (`ID`=107215 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Or donc, c\’est cette Emmarel Ombregarde qui vous envoie ? En v\’là une qui sait tenir parole, on dirait.$b$bJ\’ai jamais entendu parler du Sentier invisible dont elle se réclame, mais je suis disposé à l\’entendre.$b$bJ\’ai pas pour habitude de bosser avec vous autres de la Horde, mais la Légion veut not\’ peau à tous sans distinction, nain comme $R, alors toute aide est bonne à prendre !', `VerifiedBuild`=26365 WHERE (`ID`=107173 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Parlez, $R. Pourquoi avoir troublé mon repos ?', `VerifiedBuild`=26365 WHERE (`ID`=107107 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Shan ronir ! Allez vous faire voir chez les Taurens !', `VerifiedBuild`=26365 WHERE (`ID`=107087 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cette Gnome bouillonne d\’amertume et de rage trop longtemps contenues… Elle ne va pas tarder à exploser.', `VerifiedBuild`=26365 WHERE (`ID`=107018 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Votre Légion ne fait pas le poids face à un Orc et aux siens, démon !', `VerifiedBuild`=26365 WHERE (`ID`=106995 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Depuis que je m\’en suis servi pour tuer Garrosh… Marteau-du-Destin n\’est plus qu\‘un poids mort entre mes mains. Les éléments restent silencieux.', `VerifiedBuild`=26365 WHERE (`ID`=106994 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pressons, roggs. On ne peut pas rester ici éternellement.', `VerifiedBuild`=26365 WHERE (`ID`=106986 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Emmarel Ombregarde est une âme noble. Les Taurens de Haut-Roc sont des alliés de longue date du Sentier invisible.$b$bGriffe-Serre, la lance ayant appartenu à mon ancêtre Huln Haut-Roc, a été perdue lors d\’un combat contre un redoutable sabre-de-nuit dénommé Dakarr. Beaucoup ont tenté de la récupérer… sans succès.$b$bLes anciens affirment que c\’est en nous emparant de cette arme que nous parviendrons à chasser la Légion de nos terres.$b$bMon frère Wuho et moi partons tuer la bête et reprendre la lance. Si vous acceptiez de nous accompagner, la partie serait gagnée d\’avance !', `VerifiedBuild`=26365 WHERE (`ID`=106756 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est bien ma voix, crétin de Nain ! Je suis VIVANT !', `VerifiedBuild`=26365 WHERE (`ID`=106453 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Taurens ont-ils participé à la construction du pavillon ?', `Text1_lang`='Les Taurens ont-ils participé à la construction du pavillon ?', `VerifiedBuild`=26365 WHERE (`ID`=106369 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Bonjour $gaventurier:aventurière;, je m\’appelle Lessah. Souhaitez-vous comme moi en savoir davantage sur les Taurens de Haut-Roc ?', `VerifiedBuild`=26365 WHERE (`ID`=106302 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='On m\’a appris à considérer chaque $R comme mon ennemi juré… Mais à l\’heure actuelle, la Légion cherche à nous éradiquer, tous autant que nous sommes.$b$bC\’est pourquoi j\’ai choisi de suivre le Sentier invisible et de me rallier à votre cause.', `VerifiedBuild`=26365 WHERE (`ID`=106230 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Entendu ! Je me charge de protéger la Pierre-des-Marées. Vous, héros, tenez l\’armée d\’Elfes en respect.', `VerifiedBuild`=26365 WHERE (`ID`=106025 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je l\’appelle Strom\’kar, le Brise-Guerre.\n\nLes Arathis forgent leurs armes selon des méthodes datant de temps immémoriaux. Ce sont ces mêmes méthodes que j\’ai employées pour fabriquer mon épée à partir de métaux spéciaux. D\’un soutien infaillible dans les plus grandes batailles, elle m\’a sauvé la vie à bien des reprises et je ne compte plus les fois où elle a fait d\’une défaite inéluctable une victoire éclatante.\n\nSuite aux Guerres trolles, les Elfes m\’ont fait un cadeau unique : leurs plus grands mages et forgerons ont renforcé Strom\’kar avec de puissants enchantements. À mon avis, c\’est cette magie qui empêche la créature de se réveiller, mais je redoute ce que des siècles passés dans le corps de ce monstre ont pu faire au Brise-Guerre.', `VerifiedBuild`=26365 WHERE (`ID`=105862 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Gisant là depuis des siècles, il avait servi un mal que Tyr avait combattu bien longtemps avant. Les sceaux détruits, il a repris des forces et, à son réveil, en a appelé aux tréfonds de la terre.\n\nSurgissant du gouffre de la prison, des abominations nous ont attaqués. J\’avais fait une terrible erreur… Cette chose était plus dangereuse encore que les Trolls. Il fallait l\’arrêter avant qu\’elle ne s\’éveille entièrement.\n\nNous avons fait le sacrifice ultime. Même à peine consciente, la bête n\’a eu aucun mal à tuer mes camarades, mais leur abnégation m\’a permis d\’enfoncer Stromkar, mon épée, dans sa tête, alors même que ses griffes m\’ôtaient la vie.\n\nIl est mort, mais les sceaux ne pouvant être replacés, il finira par revenir.', `VerifiedBuild`=26365 WHERE (`ID`=105853 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ah, ça, j\’suis content de vous revoir, $gmon grand:ma grande; ! Ces Elfes ont bien besoin d\’aide !', `VerifiedBuild`=26365 WHERE (`ID`=105850 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis Thoradin, premier roi d\’Arathor.\n\nFut un temps, les Humains formaient des tribus barbares enlisées dans des guerres intestines, que les Trolls exterminaient à petit feu. Alors chef des Arathis, j\’ai unifié ces tribus et fondé le royaume d\’Arathor. Avec mes frères d\’armes Ignaeus et Lordain, j\’ai repoussé les Trolls, pour un temps.\n\nPlus tard, alliés aux Elfes, nous avons livré la guerre la plus sanglante de l\’histoire de l\’humanité. Sans l\’arrogance des Trolls, nous aurions tous péri. Au lieu de cela, nous avons gagné l\’amitié des Elfes, qui nous ont enseigné leur magie. Mais cette guerre a coûté la vie à bien des soldats et à mon ami Lordain.\n\nUne fois la paix revenue et le royaume renforcé, j\’ai abdiqué et débuté ma quête.', `VerifiedBuild`=26365 WHERE (`ID`=105841 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='$gUn:Une:r; $R !', `VerifiedBuild`=26365 WHERE (`ID`=105653 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='$gUn:Une:r; $R !', `VerifiedBuild`=26365 WHERE (`ID`=105650 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La matrice totémique est un exercice qui remonte à une époque bien antérieure à l\’invention de l\’histoire par les races mortelles. Au temps jadis, les totems n\’existaient pas. À la place, on enflammait des Gnomes. Allons, je plaisante, $gchaman:chamane;. Vous avez le droit de rire, si toutefois vous en avez envie.$b$bSous son apparente simplicité, le prochain défi mettra vos talents à rude épreuve.', `VerifiedBuild`=26365 WHERE (`ID`=105491 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous allez mourir dans cette grotte, $R !', `VerifiedBuild`=26365 WHERE (`ID`=105343 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous avez l\’air de quelqu\’un d\’intelligent, alors je vais vous donner un tuyau : laissez tomber !$b$bLe Bling-o-tron, c\’est l\’invention d\’un misérable bon à rien de petit Gnome qui comprenait rien de rien à l\’ingénierie.$b$bEt SURTOUT, n\’allez pas rendre visite à sa petite-fille, Clignose, sur le rivage de Brisesud, au sud-est. Elle est pire que lui.', `VerifiedBuild`=26365 WHERE (`ID`=105205 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’inventeur du Bling-o-tron ? Voilà une excellente question !$b$bÀ en croire la légende, c\’était un grand type, haut comme quatre Gnomes et incroyablement musclé. Notez qu\’il le fallait bien, pour pouvoir déplacer les pièces de ses inventions.$b$bIl avait de belles rouflaquettes violettes et ses yeux lançaient des éclairs !$b$bDu moins, c\’est ce que m\’a dit ma cousine Riquette.$b$bSi vous voulez en savoir plus, elle connaît le reste de l\’histoire. Vous la trouverez à K3, dans les pics Foudroyés.', `VerifiedBuild`=26365 WHERE (`ID`=105183 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je vois bien que vous n\’êtes pas de mèche avec ces nagas, $R ! Faites-moi sortir de cette cage !', `VerifiedBuild`=26365 WHERE (`ID`=105168 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand les Elfes qui ont fondé le Sentier invisible ont quitté le mont Hyjal pour regagner les îles Brisées, les Taurens de Haut-Roc ont reconnu la noblesse de leur cause et leur ont offert leur amitié. Ce sont les descendants de Huln Haut-Roc qui ont conduit le Sentier invisible jusqu\’à ce pic.$b$bEt depuis le retour de la Légion, ils nous ont aidés à réhabiliter le pavillon, notamment en remplaçant par des structures taurènes deux tours de guet endommagées.', `VerifiedBuild`=26365 WHERE (`ID`=105030 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pauvre $gpetit:petite:r; $R de rien du tout.', `VerifiedBuild`=26365 WHERE (`ID`=104985 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous ne pouvez pas nous arrêter, $R.', `VerifiedBuild`=26365 WHERE (`ID`=104974 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La Pandarie est salement amochée. Tak-Tak a plus vu autant de couics de jerbs depuis que le gros Orc a ouké le val.', `VerifiedBuild`=26365 WHERE (`ID`=104932 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Draeneï tentent de concevoir un rituel pour ramener les naarus dans la Lumière. J\’ai fait appel à d\’autres prêtres pour les y aider.', `VerifiedBuild`=26365 WHERE (`ID`=104904 AND `locale`='frFR');
