UPDATE `broadcast_text_locale` SET `Text_lang`='Votre engeance n\’est pas la bienvenue ici, $R !', `VerifiedBuild`=26365 WHERE (`ID`=104830 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais cette fois, les Pandarens ne se cacheront pas. Nous allons nous BATTRE !', `VerifiedBuild`=26365 WHERE (`ID`=104776 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Draeneï fuient la Légion ardente depuis bien longtemps. Et pendant toutes ces années, les naaru nous ont été d\’une grande aide… parfois au prix de leur vie. Sans eux, nous serions tous morts.', `VerifiedBuild`=26365 WHERE (`ID`=104674 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les mages ont eu la délicatesse d\’ouvrir un portail à notre intention. Si vous voulez bien me suivre, j\’aimerais vous parler des aaru et des Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=104673 AND `locale`='frFR');
