UPDATE `broadcast_text_locale` SET `Text_lang`='Bienvenue sur mes terres, $R. Le petit être m\’a dit que vous recherchiez des informations sur la légende d\’Irmaat.', `VerifiedBuild`=26365 WHERE (`ID`=104090 AND `locale`='frFR');
