UPDATE `broadcast_text_locale` SET `Text_lang`='Zelifrax présidera la cérémonie. Vous avez étudié les rites, Gnome ?', `VerifiedBuild`=26365 WHERE (`ID`=103744 AND `locale`='frFR');
