UPDATE `broadcast_text_locale` SET `Text_lang`='Jabrouf a trouvé tout ce dont il avait besoin. Nous ferions mieux de regagner le refuge des Elfes de la nuit.', `VerifiedBuild`=26365 WHERE (`ID`=103253 AND `locale`='frFR');
