UPDATE `broadcast_text_locale` SET `Text_lang`='Taurens, Orcs, Trolls, Elfes, Gobelins… Réprouvés. Tous, on ressent la même chose.', `VerifiedBuild`=26365 WHERE (`ID`=103221 AND `locale`='frFR');
