UPDATE `broadcast_text_locale` SET `Text_lang`='Les Taurens pensent vraiment que c\’est VOUS qui allez reprendre MON marteau ? Pathétique.', `VerifiedBuild`=26365 WHERE (`ID`=103057 AND `locale`='frFR');
