UPDATE `broadcast_text_locale` SET `Text_lang`='Vous êtes bien loin de chez vous, $gpetit:petite:r; $R.', `VerifiedBuild`=26365 WHERE (`ID`=102729 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Désormais, ces terres nous appartiennent, $R !', `VerifiedBuild`=26365 WHERE (`ID`=102719 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Khadgar ne tardera pas à vous contacter. Quant à moi, il me faut préparer Fossoyeuse pour la guerre. Dans quel camp êtes-vous, $n ? Le nôtre, ou celui des Humains ?', `VerifiedBuild`=26365 WHERE (`ID`=102443 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pendant des générations, les Taurens de cette montagne se sont battus. D\’innombrables vies, et même des tribus entières, furent perdues.', `VerifiedBuild`=26365 WHERE (`ID`=101941 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On se retrouve enfin, petit Elfe. Tu n\’es pas digne d\’être notre égal !', `VerifiedBuild`=26365 WHERE (`ID`=101905 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous avez choisi le mauvais camp, $R.', `VerifiedBuild`=26365 WHERE (`ID`=101844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Crétins de Nains… Toutes les barricades et les pierres du monde entier ne suffiront pas à vous sauver.', `VerifiedBuild`=26365 WHERE (`ID`=101791 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Petits, petits… J\’adore la viande de Nain ! Vos portes ne résisteront pas éternellement !', `VerifiedBuild`=26365 WHERE (`ID`=101790 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Brandolf ! Espèce de canaille ! J\’ai bien cru que j\’allais moisir indéfiniment dans cette cage. Qui est $gce:cette:r; $R ?', `VerifiedBuild`=26365 WHERE (`ID`=101717 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Alliance et Horde, Nains et Gnomes… On est tous issus de la même terre, des mêmes pierres. En tant que chef, vous devez transcender les différences apparentes et unir vos gens contre la Légion. L\’avenir d\’Azeroth en dépend.', `VerifiedBuild`=26365 WHERE (`ID`=101480 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous êtes $gdisposé:disposée; à écouter les élucubrations d\’un vieux Nain, j\’aimerais vous donner un conseil qu\’on m\’a inculqué quand j\’étais tout jeune.', `VerifiedBuild`=26365 WHERE (`ID`=101479 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='24e jour : Je me suis réveillé avec la lame d\’un Elfe de la nuit sous la gorge. Il m\’a demandé ce que je voulais aux flétris. Je lui ai dit que je cherchais un remède. Il a disparu sans un mot.', `VerifiedBuild`=26365 WHERE (`ID`=101433 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce que c\’est ? Les mortels osent déstabiliser mes portails et envahir mes archives ? J\’aurais dû tous vous détruire comme j\’ai détruit ce misérable petit Gnome.', `VerifiedBuild`=26365 WHERE (`ID`=101252 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Écoutez, cet incident avec la Horde de Fer n\’était qu\’un vaste malentendu. Garrosh était censé convaincre les Orcs de lutter contre la Légion ardente, pas d\’envahir Azeroth.', `VerifiedBuild`=26365 WHERE (`ID`=101173 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’accepte votre marque de respect avec humilité, $R. La sagesse que je confère à ceux qui m\’honorent est la frugalité.', `VerifiedBuild`=26365 WHERE (`ID`=101155 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’sais pas ce que les Taurens s\’imaginent, mais ça, c\’est pas des esprits. C\’est des échos temporels. Quelque chose tourne pas rond dans l\’coin.', `VerifiedBuild`=26365 WHERE (`ID`=101100 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Combien de temps la petite Gnomette va-t-elle tenir, à votre avis ? On prend les paris ?', `VerifiedBuild`=26365 WHERE (`ID`=101098 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Misérable Gobelin, ton ignorance empeste la mortalité.', `VerifiedBuild`=26365 WHERE (`ID`=101070 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Regardez la ville, $R. Un spectacle aussi beau que tragique.$b$bNous avons été bien lâches de nous terrer à l\’abri de ce bouclier. L\’avenir d\’Azeroth était entre nos mains, et nous l\’avons abandonné. Nous avons failli à notre devoir sacré et nous avons honte à présent de n\’avoir pas su protéger ce monde.', `VerifiedBuild`=26365 WHERE (`ID`=101032 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Une fois le secteur débarrassé de ces intrus, nous pourrons commencer à purger nos terres des Elfes de la nuit.', `VerifiedBuild`=26365 WHERE (`ID`=100900 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Très bien, têtu de Worgen ! Nous devons avant tout trouver un moyen de sortir d\’ici. Mais si l\’occasion d\’asservir le démon se présente, je suis d\’avis de la saisir !', `VerifiedBuild`=26365 WHERE (`ID`=100896 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu ne pourras pas garder le silence éternellement, Worgen !', `VerifiedBuild`=26365 WHERE (`ID`=100868 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Suivez-moi. Les Taurens connaissent des salles secrètes susceptibles d\’accueillir notre base d\’opérations.', `VerifiedBuild`=26365 WHERE (`ID`=100830 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Inutile de te mettre martel en tête, Gala. Henle est une jeune Taurène talentueuse, tout comme Tour. Elle a mérité de passer les épreuves et je sais qu\’elle réussira.', `VerifiedBuild`=26365 WHERE (`ID`=100622 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avez-vous attendu longtemps, $R ?', `VerifiedBuild`=26365 WHERE (`ID`=100549 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai eu le plaisir de vous voir évoluer pour intégrer le panthéon des plus grands chasseurs d\’Azeroth, jeune $R.$B$BVotre geste doit être couchée par écrit pour que tous connaissent vos exploits et l\’histoire de votre arme fabuleuse.', `VerifiedBuild`=26365 WHERE (`ID`=100429 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='On nous a envoyés ici pour surveiller les murlocs. La relève était censée arriver au bout d\’une semaine… mais nous avons débarqué le mois dernier et toujours pas d\’autres Taurens en vue. Ces deux-là commencent à me sortir par les yeux. Il ne se passe pas cinq minutes sans qu\’ils se chamaillent comme un vieux couple.', `VerifiedBuild`=26365 WHERE (`ID`=100329 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Le satyre blessé se renfrogne en vous voyant approcher. Il ne lui reste plus longtemps à vivre.>$b$bAlors, vous venez terminer ce que votre archidruide a commencé, $R ? Vous croyez avoir remporté la victoire, stupides mortels… mais Malfurion est à la merci du maître, à présent.$b$bCe crétin s\’est jeté tête baissée dans notre piège… Ha ha haaaaa…', `VerifiedBuild`=26365 WHERE (`ID`=100274 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous ne comprenez pas le chant de la sentinelle, $R ? Dans ce cas, peut-être me permettrez-vous de parler en son nom.', `VerifiedBuild`=26365 WHERE (`ID`=100239 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce que vous regardez, $R ?', `VerifiedBuild`=26365 WHERE (`ID`=100152 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='« Hélas, Votre Altesse, ce Gnome est bien seul. Mais il maîtrise toutefois certains sortilèges… »', `Text1_lang`='« Hélas, Votre Altesse, ce Gnome est bien seul. Mais il maîtrise toutefois certains sortilèges… »', `VerifiedBuild`=26365 WHERE (`ID`=100075 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Votre triste vie prend fin aujourd\’hui, $R !', `Text1_lang`='Votre triste vie prend fin aujourd\’hui, $R !', `VerifiedBuild`=26365 WHERE (`ID`=100073 AND `locale`='frFR');
