UPDATE `broadcast_text_locale` SET `Text_lang`='Allez au diable, $R ! La bière n\’attend pas.', `VerifiedBuild`=26365 WHERE (`ID`=99977 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous mettez votre nez dans des choses qui vous dépassent, Gnome !', `VerifiedBuild`=26365 WHERE (`ID`=99955 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous côtoyez les esprits d\’une contrée ancestrale, $R.', `VerifiedBuild`=26365 WHERE (`ID`=99879 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Entendu, $R, j\’enverrai les fournitures au magasin d\’enchantement. Maintenant, laissez-moi boire en paix !', `VerifiedBuild`=26365 WHERE (`ID`=99856 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faut jamais sous-estimer un Nain.', `Text1_lang`='Faut jamais sous-estimer un Nain.', `VerifiedBuild`=26365 WHERE (`ID`=99777 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un conseil, $R : avant de promettre des pots-de-vin, assurez-vous d\’abord d\’en avoir les moyens. À présent, déguerpissez, à moins que vous ne vouliez une cellule dans la citadelle Pourpre !', `VerifiedBuild`=26365 WHERE (`ID`=99715 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est une catastrophe… mais tout espoir n\’est pas perdu ! Pas tant que les chars roulent et que nos coucous sillonnent l\’espace aérien ! Le monde ne disparaîtra pas sans que les Gnomes aient leur mot à dire !', `VerifiedBuild`=26365 WHERE (`ID`=99644 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Gardez les mains bien en vue, pas de blague, $R !', `Text1_lang`='Gardez les mains bien en vue, pas de blague, $R !', `VerifiedBuild`=26365 WHERE (`ID`=99490 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu es venu récupérer ton petit Draeneï abîmé ?', `VerifiedBuild`=26365 WHERE (`ID`=99173 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Khadgar et son « conseil » font peut-être la loi à la surface, mais ici-bas, mes gars et moi sommes les seuls maîtres à bord.$b$bNe faites pas de vagues tant que vous serez dans mes égouts, $R. À moins bien sûr que vous ayez les arguments pour me convaincre de fermer les yeux…', `VerifiedBuild`=26365 WHERE (`ID`=99084 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Autant apprendre à un sanglier à voler que de tenter d\’inculquer les rudiments de la chasse à un Gnome.', `VerifiedBuild`=26365 WHERE (`ID`=99040 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai peut-être perdu mon pari, mais vos talents de $gchasseur:chasseuse; sont indéniables.\n\nLa Gnomette et vous complétez admirablement notre groupe.', `VerifiedBuild`=26365 WHERE (`ID`=99035 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous êtes plutôt $gdoué:douée; pour $gun:une:r; $R.', `VerifiedBuild`=26365 WHERE (`ID`=99033 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne jamais chercher des noises à un Gobelin armé d\’une clé à molette !', `VerifiedBuild`=26365 WHERE (`ID`=98998 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Connaissez-vous l\’histoire du cerf blanc et de la lune ? On raconte que les Taurens traquaient Malorne, qui manqua d\’être capturé. Pour échapper à ses poursuivants, il bondit haut dans le ciel, mais sa ramure se prit dans les étoiles.$b$bIl eut beau se débattre, il ne put s\’en dégager. Quand Élune, la déesse de la lune, le vit ainsi, elle lui proposa de le libérer si, en échange, il acceptait de l\’aimer et de mettre fin à sa solitude.$b$bMais ce n\’est là que l\’une des nombreuses versions du mythe, un récit transformé peu à peu par les fiers chasseurs qui se le transmettent depuis des générations.', `Text1_lang`='Connaissez-vous l\’histoire du cerf blanc et de la lune ? On raconte que les Taurens traquaient Malorne qui manqua d\’être capturé. Pour échapper à ses poursuivants, Malorne bondit haut dans le ciel, mais sa ramure se prit dans les étoiles.$b$bIl eut beau se débattre, il ne put s\’en dégager. Quand Élune, la déesse de la lune, le vit ainsi, elle lui proposa de le libérer si, en échange, il acceptait de l\’aimer et de mettre fin à sa solitude.$b$bMais ce n\’est là que l\’une des nombreuses versions du mythe, un récit transformé peu à peu par les fiers chasseurs qui se le transmettent depuis des générations.', `VerifiedBuild`=26365 WHERE (`ID`=98815 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nos forêts sont envahies par les satyres. Ces anciens Elfes de la nuit ont prêté hommage lige à la Légion et la magie démoniaque les a pervertis. Pareilles créatures n\’ont pas leur place dans la nature.', `Text1_lang`='Nos forêts sont envahies par les satyres. Ces anciens Elfes de la nuit ont prêté hommage lige à la Légion et la magie démoniaque les a pervertis. Pareilles créatures n\’ont pas leur place dans la nature.', `VerifiedBuild`=26365 WHERE (`ID`=98809 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sur Azsuna se dressait autrefois la fabuleuse cité de Nar\’thalas, l\’un des joyaux de la civilisation des Elfes de la nuit. C\’est un endroit idéal depuis lequel mener l\’assaut contre la Légion.', `VerifiedBuild`=26365 WHERE (`ID`=98768 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’apprécie votre générosité, $R. Ces potions seront très utiles.', `VerifiedBuild`=26365 WHERE (`ID`=98563 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Salutations, $gchampion:championne;.$b$bNous manquons cruellement d\’alliés sur ces terres, et la tribu de Haut-Roc est la seule dans toute l\’histoire des Taurens à s\’être dressée contre la Légion ardente.$b$bJe suis ici pour l\’aider à s\’unir à nouveau sous le même totem, afin de nous rejoindre dans notre lutte contre la Légion.', `VerifiedBuild`=26365 WHERE (`ID`=98549 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='La précision, c\’est l\’harmonie, $R.', `VerifiedBuild`=26365 WHERE (`ID`=98316 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Dites-moi, $R, quelle bête de Draenor garde votre cœur ?', `VerifiedBuild`=26365 WHERE (`ID`=98202 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Massacrez les Elfes de la nuit et leur champion ! Arrosez la terre de leur sang !', `VerifiedBuild`=26365 WHERE (`ID`=98147 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Frappez maintenant ! Concentrez-vous sur le champion des Elfes de la nuit !', `VerifiedBuild`=26365 WHERE (`ID`=98144 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je me demande si les Elfes de la nuit pleureront votre mort.', `VerifiedBuild`=26365 WHERE (`ID`=98096 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous allez voir, stupides Elfes. Je vais écraser votre champion.', `VerifiedBuild`=26365 WHERE (`ID`=97934 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous cherchez ce timbré d\’Elfe ? Ouais, je l\’ai vu. Il était là, à faire un raffut de tous les diables en bâfrant une espèce de poudre magique ou je ne sais quoi.$b$bJe l\’ai chassé vite fait bien fait ; les malotrus dans son genre ne sont pas les bienvenus devant mon magasin !', `VerifiedBuild`=26365 WHERE (`ID`=97869 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Voilà un cadeau digne de ce nom, $R. Montrez-moi l\’étoffe que vous souhaitez ravauder.', `VerifiedBuild`=26365 WHERE (`ID`=97845 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ouais, je l\’ai vu, en effet. Votre ami l\’Elfe s\’est introduit ici pour chaparder un de mes brûle-flammes telluriques, le pendard ! Je l\’ai pourchassé jusque dans les égouts avant qu\’il me glisse entre les doigts.\n\nSi vous le trouvez, veillez à ce qu\’il me rende mon brûle-flamme. C\’est un objet très rare.', `VerifiedBuild`=26365 WHERE (`ID`=97839 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Tous les enfants Elfes de la nuit apprennent à aimer l\’obscurité.', `VerifiedBuild`=26365 WHERE (`ID`=97699 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je vais réduire vos pitoyables Worgens en charpie, Grisetête !', `VerifiedBuild`=26365 WHERE (`ID`=97397 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est la mort que tu cherches, Elfe ? Oseras-tu affronter mon seigneur ?', `VerifiedBuild`=26365 WHERE (`ID`=97374 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nos routes se sont-elles déjà croisées, $ghéros:héroïne; ?$B$BIl y a dix mille ans de cela, j\’étais à la tête de l\’armée d\’Elfes de la nuit qui défendait cette contrée contre la Légion ardente.$B$BMa sœur n\’est autre que Maiev la gardienne, et c\’est à cause d\’elle que je suis ici aujourd\’hui.', `VerifiedBuild`=26365 WHERE (`ID`=97341 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je vous félicite d\’avoir éliminé le misérable Worgen qu\’était Grisetête.\n\nMon seul regret est de ne pas l\’avoir vu rendre son dernier soupir.', `VerifiedBuild`=26365 WHERE (`ID`=97241 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je vous félicite d\’avoir éliminé le misérable Worgen qu\’était Grisetête.\n\nMon seul regret est de ne pas l\’avoir vu rendre son dernier soupir.', `VerifiedBuild`=26365 WHERE (`ID`=97240 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La profanation du tombeau, la présence de démons ici, au bastion du Freux, et ces Elfes de la nuit qui nous attaquent… cela ne peut être une coïncidence.$B$BLa Légion a ressuscité le seigneur Corvaltus. Mon ancien maître est de retour parmi les vivants.', `VerifiedBuild`=26365 WHERE (`ID`=97196 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Sales petits fouineurs de Worgens, vous auriez dû laisser les flammes vous consumer.', `VerifiedBuild`=26365 WHERE (`ID`=97172 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Gardiennes quadrillent les îles Brisées à la recherche de Gul\’dan.$B$BTout porte à croire qu\’il cache quelque secret dans l\’enceinte du bastion du Freux. Et s\’il est ici, ma sœur Maiev est peut-être dans les parages, elle aussi.', `VerifiedBuild`=26365 WHERE (`ID`=96986 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Préparez-vous à mourir une seconde fois, $gMort-vivant:Morte-vivante; de malheur !', `Text1_lang`='Préparez-vous à mourir une seconde fois, $gMort-vivant:Morte-vivante; de malheur !', `VerifiedBuild`=26365 WHERE (`ID`=96976 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='En ce qui me concerne, je me méfie des Gnomes armés.\n\nAddie a plus de chances de vous tirer dessus que d\’atteindre ses cibles.', `VerifiedBuild`=26365 WHERE (`ID`=96824 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous êtes tombés sur le mauvais Gnome, démons !', `VerifiedBuild`=26365 WHERE (`ID`=96753 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sales Worgens… Une bête a beau être habillée, ça n\’en reste pas moins une bête.', `VerifiedBuild`=26365 WHERE (`ID`=96542 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Chasse ces Taurens de ma montagne.', `VerifiedBuild`=26365 WHERE (`ID`=96344 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='$gLe:La:r; $R émet un grondement sourd tout en rassemblant ses forces en vue du combat.', `VerifiedBuild`=26365 WHERE (`ID`=96334 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Arrêtez de mentir, Elfe de la nuit. Vous resterez là où nous pouvons vous surveiller.', `VerifiedBuild`=26365 WHERE (`ID`=96102 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Longue vie à la Légion ! Meurs, sale $R !', `Text1_lang`='Longue vie à la Légion ! Meurs, sale $R !', `VerifiedBuild`=26365 WHERE (`ID`=95994 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vos os se briseront sous ma botte, $R !', `Text1_lang`='Vos os se briseront sous ma botte, $R !', `VerifiedBuild`=26365 WHERE (`ID`=95992 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les autres ont évoqué $gun:une:c; $c, $gun:une:r; $R sachant coudre. J\’imagine que c\’est de vous qu\’il s\’agit.$b$bSi Tytallo estime que vous êtes $gprêt:prête;, alors il est temps de vous montrer le métier à tisser régalien.', `VerifiedBuild`=26365 WHERE (`ID`=95898 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous ne voulez pas d\’ennuis, gardez vos distances avec la citadelle Pourpre, $R. Les créatures qui y sont confinées ne me disent rien qui vaille…', `VerifiedBuild`=26365 WHERE (`ID`=95864 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça m\’a tout l\’air d\’une étoffe d\’Elfe de la nuit… Attends une minute…', `VerifiedBuild`=26365 WHERE (`ID`=95657 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Oui. Les Draeneï mèneront l\’assaut sur la porte principale pendant que votre flotte les prend en tenaille.', `VerifiedBuild`=26365 WHERE (`ID`=95583 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Mes sœurs sont quelque part, là dehors, $p. À présent, elles sont plus démons qu\’Elfes, mais elles restent mineuses avant tout. Si vous les croisez au cours de vos voyages, je vous saurais gré de m\’en informer.', `VerifiedBuild`=26365 WHERE (`ID`=95534 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Votre bien-aimé Vivevol est mort ! Vous avez réussi à survivre à la chute, Elfe de la nuit. Vous avez eu la chance de votre vie.', `VerifiedBuild`=26365 WHERE (`ID`=95419 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rosh l\’Affable vous aime bien, $gpetit:petite:r; $R. Rosh l\’Affable va partir maintenant.', `VerifiedBuild`=26365 WHERE (`ID`=95179 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='$gPetit:Petite:r; $R est $gami:amie; de Rosh l\’Affable.\n\nVous restez ?', `VerifiedBuild`=26365 WHERE (`ID`=95166 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Y a pas l\’ombre d\’un Tauren à cuire à la broche dans le coin. T\’avais promis qu\’y aurait des Taurens à rôtir.', `VerifiedBuild`=26365 WHERE (`ID`=94983 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Y a rien dans ce coin, que des moutons et des Vrykuls. T\’avais dit qu\’il y aurait des bons petits Gnomes à se mettre sous la ratiche.', `VerifiedBuild`=26365 WHERE (`ID`=94982 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a des choses bizarres, là-haut. Des méchants Draeneï tout brillants trafiquent les loups et invoquent des démons. Je crois qu\’ils ont tué tous les Draeneï normaux.$b$bVous pourriez pas leur faire tomber un très gros rocher sur la tête ?', `VerifiedBuild`=26365 WHERE (`ID`=94844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Gul\’dan transforme des Orcs en bêtes monstrueuses !', `VerifiedBuild`=26365 WHERE (`ID`=94823 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Durotan m\’a envoyé ici pour apaiser les tensions entre les survivants du Crâne-Ricanant et les Draeneï.$b$b<Frogan contemple avec dégoût le camp de réfugiés crasseux du Crâne-Ricanant.>$b$bJe préférerais être sur le front.', `VerifiedBuild`=26365 WHERE (`ID`=94770 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ne traînez pas, $R. Le temps nous est compté, il n\’y a pas une minute à perdre.', `VerifiedBuild`=26365 WHERE (`ID`=94717 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='M\’est d\‘avis qu\’on va pas tarder à le savoir.', `VerifiedBuild`=26365 WHERE (`ID`=94232 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est notre trésor, sale $R !', `VerifiedBuild`=26365 WHERE (`ID`=94217 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous l\’ignorez ? Vous êtes à Helheim, $R… Votre âme appartient désormais à Helya. Nul ne quitte cet endroit sans l\’accord de la déesse.$b$bD\’aucuns affirment que seuls les cadavres qui se battent sur le champ de bataille Éternel et qui lui prouvent leur valeur peuvent partir.$b$bMais il est impossible de traverser le champ de bataille sans affronter Guarm, qui protège férocement sa déesse.$b$bJ\’ai essayé de me faufiler sous sa truffe plus de fois que vous ne pouvez l\’imaginer, mais l\’issue est toujours la même : je finis déchiqueté et jeté en pâture aux chiens.', `VerifiedBuild`=26365 WHERE (`ID`=94149 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Imbécile de Nain. Livrez sa tête à l\’avant-poste !', `VerifiedBuild`=26365 WHERE (`ID`=94105 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs de l\’Orbite-Sanglante sont de redoutables chasseurs et trappeurs. Ils sont parfaitement accoutumés à la vie dans la jungle. Au cœur de leur village, ils pratiquent une sombre magie liée au sang. Cela ne présage rien de bon et je recommande de réduire cet endroit en cendres à la première opportunité !', `VerifiedBuild`=26365 WHERE (`ID`=94066 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous avons entamé le siège dès que vous avez pris possession des navires. Tout laissait à penser que la bataille serait longue et pénible, jusqu\’à ce qu\’un important contingent de leurs forces quitte soudainement les lieux. Sans doute au moment où vous avez attaqué depuis l\’est. $b$b\nNous les avons alors frappés de plein fouet. Thaelin est arrivé avec un bélier mécanique et les portes n\’ont pas tenu longtemps. Nous sommes ensuite tombés nez à nez avec cette nouvelle menace… les Gangr\’orcs. Ils nous pressent de toutes parts et nous sommes coincés ici. J\’espère que les choses se passent mieux pour vous, à l\’est.', `VerifiedBuild`=26365 WHERE (`ID`=94058 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Besoin d\’aide avec votre liste de course ?\n\nJe déteste encourager le vol, mais les Ogres qui font la fête dans l\’arène de Sang adorent leur caisson de basses. Ils ne sont pas là pour se battre, mais ne traînez pas après le leur avoir pris !\n\nOn m\’a volé un Laz-tron il y a quelques semaines et ça puait le Gobelin à des kilomètres. Allez donc faire un tour au poste de Sifflepince.\n\nDifficile de trouver un amplificateur phonique, mais un morceau de métal d\’un assemblage arakkoa devrait suffire.\n\nSi vous comptez en découdre avec la Horde de Fer, leurs déchiqueteurs en Talador sont équipés du condensateur dont j\’ai besoin.\n\nEt pour finir, prenez le bois des anciens de Gorgrond, si les commandojacks en ont épargné quelques uns.', `Text1_lang`='Besoin d\’aide avec votre liste de course ?\n\nJe déteste encourager le vol, mais les Ogres qui font la fête dans l\’arène de Sang adorent leur caisson de basses. Ils ne sont pas là pour se battre, mais ne traînez pas après le leur avoir pris !\n\nOn m\’a volé un Laz-tron il y a quelques semaines et ça puait le Gobelin à des kilomètres. Allez donc faire un tour au poste de Sifflepince.\n\nDifficile de trouver un amplificateur phonique, mais un morceau de métal d\’un assemblage arakkoa devrait suffire.\n\nSi vous comptez en découdre avec la Horde de Fer, leurs déchiqueteurs en Talador sont équipés du condensateur dont j\’ai besoin.\n\nEt pour finir, prenez le bois des anciens de Gorgrond, si les commandojacks en ont épargné quelques uns.', `VerifiedBuild`=26365 WHERE (`ID`=93780 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La musique des Trolls est… intéressante. Ils conservent leurs meilleures partitions à Zul\’Gurub.', `VerifiedBuild`=26365 WHERE (`ID`=93745 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Dix mille ans de damnation pour dix mille Elfes. Tout ça à cause de vos alliances imprudentes et de vos manœuvres hasardeuses.', `VerifiedBuild`=26365 WHERE (`ID`=93396 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On dirait que Gul\’dan rassemble toutes les troupes de la Horde de Fer de Draenor. Elles arrivent par bateau au port… puis vont à l\’est… en direction des fosses.$b$bEt là, elles sont… converties… en Gangr\’orcs. C\’est abominable. Il faut détruire cet endroit au plus vite !', `VerifiedBuild`=26365 WHERE (`ID`=92989 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous avons entamé le siège dès l\’instant où vous avez pris possession des navires. Nous avons combattu avec prudence jusqu\’au départ d\’un important contingent de la Horde de Fer, qui est certainement parti vous affronter. Ce n\’est qu\’à cet instant que nous sommes vraiment montés à l\’assaut !$b$bVotre ami Gazleu a mis au point un terrible engin pour détruire les portes et nous nous sommes engouffrés dans la brèche… pour tomber nez à nez avec cette nouvelle menace… les Gangr\’orcs. Ils ont réussi à stopper net notre élan et à nous coincer ici. J\’espère que les choses se passent mieux pour vous, à l\’est.', `VerifiedBuild`=26365 WHERE (`ID`=92970 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces nouveaux Gangr\’orcs livrent une lutte acharnée. Tout ceci n\’est pas près de se terminer…', `VerifiedBuild`=26365 WHERE (`ID`=92969 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Jouer « Le Fardeau des Worgens »', `Text1_lang`='Jouer « Le Fardeau des Worgens »', `VerifiedBuild`=26365 WHERE (`ID`=92959 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Orcs de l\’Orbite-Sanglante pullulent par ici. Mais où est leur chef ?', `VerifiedBuild`=26365 WHERE (`ID`=92932 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces murs, chef... et ces Orcs qui nous barrent la route... c\’est quelque chose !', `VerifiedBuild`=26365 WHERE (`ID`=92908 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Veuillez excuser mes petites-filles. Elles pensent qu\’il est nécessaire de me protéger chaque fois $gqu\’un:qu\’une:r; $R $c approche mes bassins.$b$bAllez-y, continuez.', `VerifiedBuild`=26365 WHERE (`ID`=92792 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La compassion ? Cela fait bien longtemps que j\’ai oublié le sens de ce mot. Je m\’en souviendrai, $R.', `VerifiedBuild`=26365 WHERE (`ID`=92781 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Orcs ont abandonné des machines de siège ici, dans l\’atelier de siège de Fer.', `VerifiedBuild`=26365 WHERE (`ID`=92763 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Fouillez les Orcs à la recherche d\’indices.', `Text1_lang`='Fouillez les Orcs à la recherche d\’indices.', `VerifiedBuild`=26365 WHERE (`ID`=92671 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous ne pouvons pas quitter notre foyer, $R. Nous poursuivrons le cycle de notre vie sans vous.', `VerifiedBuild`=26365 WHERE (`ID`=92231 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Que de pensées intéressantes ! Montrez-nous en plus, $R. Tuez les autres, ici ! Nourrissons-nous de nos ennemis, exploitons leurs machines et découvrons les merveilles que renferme notre foyer !', `VerifiedBuild`=26365 WHERE (`ID`=92218 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’était au temps où l\’influence de la reine Azshara sur les Elfes était à son apogée. Elle abusait de la magie, ce qui la rendait aussi puissante que dangereuse.', `VerifiedBuild`=26365 WHERE (`ID`=92207 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tiens, $gun:une:r; $R. Voilà qui nous change. Approchez donc, mon enfant.', `VerifiedBuild`=26365 WHERE (`ID`=92155 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Soyez maudits, toi et ta race, Elfe. La reine en personne entendra parler de ceci !', `VerifiedBuild`=26365 WHERE (`ID`=91816 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nos frères sont retenus prisonniers, $R. Aidez-nous à les libérer, je vous en prie. Entrez dans les ruines et arrachez nos semblables aux griffes de leurs oppresseurs. Les faire réintégrer nos rangs aidera énormément notre cause ; mais pour cela, vous devez les mener sains et saufs auprès d\’un sombrevoyant !', `VerifiedBuild`=26365 WHERE (`ID`=91739 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je sais ce que vous mijotez, vous et votre pote, là. Vous croyez pouvoir vous tailler une part du gâteau ?\n\nAucune chance.\n\nJe vous assure qu\’avant ce soir, vous ramperez sur le sol de l\’arène avec ma botte sur la nuque, $R.', `VerifiedBuild`=26365 WHERE (`ID`=91627 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce $gqu\’un petit:qu\’une petite:r; $R comme vous fait ici ?\n\nOh, je sens qu\’on va bien se marrer, ça oui.', `VerifiedBuild`=26365 WHERE (`ID`=91622 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de cet endroit sont bien moins accueillants que moi. Restez sur vos gardes.', `VerifiedBuild`=26365 WHERE (`ID`=91613 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En ce qui me concerne, je suis plutôt peaux et fourrures, mais si vous préférez travailler les tissus plus délicats, vous devriez vous rapprocher de Saesha Sang-d\’Argent, une Elfe de sang.\n\nElle s\’est installée avec ceux de son peuple, en face du quartier tauren.', `Text1_lang`='En ce qui me concerne, je suis plutôt peaux et fourrures, mais si vous préférez travailler les tissus plus délicats, vous devriez vous rapprocher de Saesha Sang-d\’Argent, une Elfe de sang.\n\nElle s\’est installée avec ceux de son peuple, en face du quartier tauren.', `VerifiedBuild`=26365 WHERE (`ID`=91568 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Taurens qui sont arrivés avec nous semblent s\’y connaître parfaitement en travail du cuir.\n\nAllez voir Burga Cuir-Solide dans leur quartier, si vous avez besoin d\’une formation.', `Text1_lang`='Les Taurens qui sont arrivés avec nous semblent s\’y connaître parfaitement en travail du cuir.\n\nAllez voir Burga Cuir-Solide dans leur quartier, si vous avez besoin d\’une formation.', `VerifiedBuild`=26365 WHERE (`ID`=91565 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un petit groupe d\’Elfes de sang a établi un camp à côté des Taurens.\n\nVous y trouverez un joaillier du nom d\’Alixandre Vif-Acier.', `Text1_lang`='Un petit groupe d\’Elfes de sang a établi un camp à côté des Taurens.\n\nVous y trouverez un joaillier du nom d\’Alixandre Vif-Acier.', `VerifiedBuild`=26365 WHERE (`ID`=91564 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous cherchez de l\’aide en herboristerie, vous devriez vous rendre dans le quartier des Morts-vivants.\n\nAnthony Allain se trouve près de la hutte de l\’alchimiste. Il vous apprendra peut-être un truc ou deux.', `Text1_lang`='Si vous cherchez de l\’aide en herboristerie, vous devriez vous rendre dans le quartier des Morts-vivants.\n\nAnthony Allain se trouve près de la hutte de l\’alchimiste. Il vous apprendra peut-être un truc ou deux.', `VerifiedBuild`=26365 WHERE (`ID`=91562 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Brix Lanceroquette est le Gobelin qui s\’y connaît le mieux en pêche, dans le coin.\n\nVous le trouverez sur sa petite île, au nord du quartier gobelin.', `Text1_lang`='Brix Lanceroquette est le Gobelin qui s\’y connaît le mieux en pêche, dans le coin.\n\nVous le trouverez sur sa petite île, au nord du quartier gobelin.', `VerifiedBuild`=26365 WHERE (`ID`=91561 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est un Pandaren, Wei Prompte Guérison, qui s\’occupe de nos grunts. C\’est à lui qu\’il faut vous adresser pour des cours de secourisme.\n\nVous le trouverez dans la caserne. Vous n\’avez qu\’à suivre le bruit des Orcs à l\’entraînement.', `Text1_lang`='C\’est un Pandaren, Wei Prompte Guérison, qui s\’occupe de nos grunts. C\’est à lui qu\’il faut vous adresser pour des cours de secourisme.\n\nVous le trouverez dans la caserne. Vous n\’avez qu\’à suivre le bruit des Orcs à l\’entraînement.', `VerifiedBuild`=26365 WHERE (`ID`=91560 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce sont les Trolls qui gèrent tous nos besoins en enchantements à Fer-de-Lance.\n\nAllez voir Hane\’ke dans le cabinet d\’enchanteur, dans leur quartier. Il devrait pouvoir vous aider.', `Text1_lang`='Ce sont les Trolls qui gèrent tous nos besoins en enchantements à Fer-de-Lance.\n\nAllez voir Hane\’ke dans le cabinet d\’enchanteur, dans leur quartier. Il devrait pouvoir vous aider.', `VerifiedBuild`=26365 WHERE (`ID`=91557 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le portail vers Darnassus se situe près du puits de lune des Elfes de la nuit, du côté est de Bouclier-des-Tempêtes.\n\nSuivez la route qui part de l\’hôtel des ventes pour l\’atteindre.', `Text1_lang`='Le portail vers Darnassus se situe près du puits de lune des Elfes de la nuit, du côté est de Bouclier-des-Tempêtes.\n\nSuivez la route qui part de l\’hôtel des ventes pour l\’atteindre.', `VerifiedBuild`=26365 WHERE (`ID`=91467 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de la nuit tentent de ressusciter un ancien à l\’aide de la puissance des artéfacts d\’A\’shran pour utiliser cette créature contre nous.$B$BAlors, si vous rencontrez des membres de l\’Alliance, débrouillez-vous comme vous voulez, mais prenez-leur les fragments qu\’ils transportent.', `VerifiedBuild`=26365 WHERE (`ID`=91432 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne sais pas comment vous faites pour supporter une discussion avec ce sauvage de Zurge. Il est tellement barbare, même pour un Orc.', `VerifiedBuild`=26365 WHERE (`ID`=91281 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le pouvoir aux Gnomes !', `VerifiedBuild`=26365 WHERE (`ID`=91093 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Craignez la puissance des Gnomes !', `VerifiedBuild`=26365 WHERE (`ID`=91092 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour Lune-d\’Argent !', `VerifiedBuild`=26365 WHERE (`ID`=91071 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Nous devons protéger la terre sacrée des Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=91048 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Tu es un Orc vert bien étrange.', `VerifiedBuild`=26365 WHERE (`ID`=91021 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Salutations, $R.$B$BAu vu de votre allure, je vois que vous n\’avez rien d\’$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors qu\’ils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : d\’autres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?', `Text1_lang`='Salutations, $R.$B$BAu vu de votre allure, je vois que vous n\’avez rien d\’$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors qu\’ils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : d\’autres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?', `VerifiedBuild`=26365 WHERE (`ID`=90961 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne vous disputez jamais avec un Tauren, la situation irait de mal en pis.', `VerifiedBuild`=26365 WHERE (`ID`=90946 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La boisson préférée des Pandarens est le huojin tonic.', `VerifiedBuild`=26365 WHERE (`ID`=90941 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand un Tauren joue à cache-cache, on dit qu\’il steak haché.', `VerifiedBuild`=26365 WHERE (`ID`=90939 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand l\’Orc a sommeil, il écrase. Quand le Gnome a sommeil, l\’Orc l\’écrase.', `VerifiedBuild`=26365 WHERE (`ID`=90935 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pas maintenant ! Ces Humains ont apporté toute cette viande terriblement appétissante ! Imaginez-vous y planter vos crocs jusqu\’à l\’en déchirer. Le sang qui gicle et le bruit de la chair qui cède tandis que vous la dévorez…\n\n<Les yeux du Worgen se perdent dans le vague…>\n\nVous vouliez quoi, déjà ?', `VerifiedBuild`=26365 WHERE (`ID`=90930 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ces Trolls sont pas si mauvais ! D\’accord, ils sont maigres et parlent bizarrement, mais comme lanciers, il n\’y a pas plus marrant !\n\nJe suis impatiente de m\’en servir pour embrocher quelques nains !', `VerifiedBuild`=26365 WHERE (`ID`=90812 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nostalgique ? Comment est-ce que cette urine de Troll peut te rendre nostalgique ?', `VerifiedBuild`=26365 WHERE (`ID`=90806 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Orcs ne boivent donc rien d\’autre que de la bière ? Je suis un Gobelin, il me faut un cocktail, c\’est quand même pas trop demander !', `VerifiedBuild`=26365 WHERE (`ID`=90804 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avez-vous goûté la nourriture locale ? Ces Draeneï savent cuisiner, ça ne fait pas un pli.', `VerifiedBuild`=26365 WHERE (`ID`=90789 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous autres, les Draeneï, sommes la race élue… Nous sommes civilisés, cultivés et d\’une intelligence supérieure.$B$BIl n\’est pas de technologie qui puisse défier notre intellect, ni de magie dont nos arcanistes ne parviennent à se rendre maîtres.$B$BNous avions fondé en Argus une civilisation tout simplement remarquable.$B$BLes nôtres n\’ont jamais été des esclaves, ni des lâches refusant d\’assumer leur véritable potentiel.$B$BNotre peuple a les moyens de conquérir jusqu\’aux étoiles elles-mêmes !$B$BUnissons-nous, mes frères et sœurs, et saisissons notre chance à bras-le-corps !', `VerifiedBuild`=26365 WHERE (`ID`=90569 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rien ne doit interrompre l\’entreprise de la Légion. Certains en Draenor cherchent à contrecarrer ses plans. En tête de file se trouvent les infidèles, des Draeneï qui vénèrent la Lumière.$B$BExterminez nos semblables et veillez à ce que rien n\’entrave l\’œuvre de nos maîtres. Nous devons triompher des Draeneï, afin de prouver notre loyauté à la Légion. Écrasez Shattrath, et les démons nous offriront l\’immortalité !', `VerifiedBuild`=26365 WHERE (`ID`=90568 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Z\’allez dire à cet Elfe qu\’il a qu\’à se trouver des volontaires. Et qu\’il fasse vite, parce qu\’il commence à me sortir par les yeux !', `VerifiedBuild`=26365 WHERE (`ID`=90547 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Cet Elfe, Belloc, il dit qu\’il a besoin de renforts pour trouver le gros artéfact.', `VerifiedBuild`=26365 WHERE (`ID`=90545 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tout ce que je veux savoir, c\’est quand nous aurons l\’occasion de combattre de nouveau les Humains. Ça commence à me démanger méchamment !', `VerifiedBuild`=26365 WHERE (`ID`=90510 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous avez entendu les rumeurs ? Les Elfes de sang auraient découvert un truc énorme dans les mines.', `VerifiedBuild`=26365 WHERE (`ID`=90505 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les exarques sont chargés de guider les Draeneï. Nous sommes les gardiens des valeurs sur lesquelles repose notre société.', `VerifiedBuild`=26365 WHERE (`ID`=90493 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le clan Ombrelune fait honte à tous les Orcs. Les victimes que nous faisons parmi ses membres adressent un message clair à leurs semblables.', `VerifiedBuild`=26365 WHERE (`ID`=90382 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et on appelle ça un commandant… hic ! Je bosse avec les Orcs depuis… hic… le début !', `VerifiedBuild`=26365 WHERE (`ID`=90370 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et les Draeneï, pour ne pas parler d\’eux ? Ou plutôt, « l\’autre race bleue », comme ils aiment à se nommer. Comment faire confiance à des gars qui se font des marteaux de guerre avec des gemmes rose bonbon ?!', `VerifiedBuild`=26365 WHERE (`ID`=90343 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Attention, les gars de l\’Alliance ne sont pas tous des sales types. Si vous aimez la rigolade et les curiosités linguistiques, allez donc boire un coup avec un Nain !', `VerifiedBuild`=26365 WHERE (`ID`=90341 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ouais ? Quoi ? Ce rata des combattants est un secret de famille.\n\nFrouks, je le donne à personne, $R.', `VerifiedBuild`=26365 WHERE (`ID`=90311 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces gars de la Horde de Fer croient s\’y connaître en mécanique. \n\nAttendez un peu qu\’ils voient ce dont les Gobelins sont capables, quand on mettra la main sur leurs inventions !', `VerifiedBuild`=26365 WHERE (`ID`=90176 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous ne sommes pas du même bord, $R. Merci de votre aide, mais je dois prendre congé.', `Text1_lang`='Nous ne sommes pas du même bord, $R. Merci de votre aide, mais je dois prendre congé.', `VerifiedBuild`=26365 WHERE (`ID`=90134 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Merci, $R, mais je ne compte pas rallier votre camp.', `Text1_lang`='Merci, $R, mais je ne compte pas rallier votre camp.', `VerifiedBuild`=26365 WHERE (`ID`=90133 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai pour mission d\’entraîner les gardiens d\’Anzu, $R. Puis-je compter sur votre soutien ?', `VerifiedBuild`=26365 WHERE (`ID`=90070 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ha ha, cette mascotte est aussi pathétique que vous, $R.', `VerifiedBuild`=26365 WHERE (`ID`=90051 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Comme je m\’y attendais, $R, vous êtes trop faible.', `VerifiedBuild`=26365 WHERE (`ID`=90031 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='HA ! Hé, Jhorn, vous rappeler la fois où moi cracher glaviot en forme |2 $R ? $n ressembler à ça, maintenant !', `VerifiedBuild`=26365 WHERE (`ID`=90000 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça être bébé $R ou $gvrai:vraie:r; $R taille adulte ? Ça mourir beaucoup trop vite !', `VerifiedBuild`=26365 WHERE (`ID`=89999 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Houla ! $gPetit:Petite:r; $R connaître mort atroce !', `VerifiedBuild`=26365 WHERE (`ID`=89997 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Fini pour $n ! Voilà $R qui laisser vilaines traces au sol !', `VerifiedBuild`=26365 WHERE (`ID`=89993 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Pourquoi faut-il toujours que les Orcs s\’entraînent avec des haches aiguisées ?', `VerifiedBuild`=26365 WHERE (`ID`=89971 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tout ce que je sais, c\’est que l\’Alliance, elle les veut, et que les Elfes, ils pensent que ces artéfacts sont super puissants. On tient pas à ce que l\’Alliance mette la main là-dessus.', `VerifiedBuild`=26365 WHERE (`ID`=89969 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les elekk sont les créatures de Draenor dotées de la plus longue espérance de vie, et les Orcs connaissent apparemment Dekorhan depuis des générations…', `VerifiedBuild`=26365 WHERE (`ID`=89908 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Gloire au nouveau chef de guerre ! Même si ce n\’est pas un Orc.', `VerifiedBuild`=26365 WHERE (`ID`=89763 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Orcs sont-ils vivants ou morts-vivants ?', `Text1_lang`='Ces Orcs sont-ils vivants ou morts-vivants ?', `VerifiedBuild`=26365 WHERE (`ID`=89671 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Venir à Fer-de-Lance c\‘était une grosse erreur.', `VerifiedBuild`=26365 WHERE (`ID`=89507 AND `locale`='frFR');
