UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous êtes $gexalté:exaltée; auprès de Fossoyeuse, des Pitons-du-Tonnerre, des Trolls sombrelances, de Lune-d\’Argent, du port Baille-Fonds, des Pandarens huojin ou si vous êtes un chevalier de la mort exalté auprès des chevaliers de la Lame d\’ébène, vous pourrez recruter des gardes dans leurs rangs respectifs en parlant à l\’officier recruteur correspondant dans la grande salle.', `Text1_lang`='Si vous êtes $gexalté:exaltée; auprès de Fossoyeuse, des Pitons-du-Tonnerre, des Trolls sombrelances, de Lune-d\’Argent, du port Baille-Fonds, des Pandarens huojin ou si vous êtes un chevalier de la mort exalté auprès des chevaliers de la Lame d\’ébène, vous pourrez recruter des gardes dans leurs rangs respectifs en parlant à l\’officier recruteur correspondant dans la grande salle.', `VerifiedBuild`=26365 WHERE (`ID`=89258 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Haha ! Toi, c\’était la première fois que tu croisais un Nain chevalier de la mort, hein ?', `VerifiedBuild`=26365 WHERE (`ID`=89130 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Autrefois, j\’arpentais ces salles avec fierté, la tête haute, comme se doit de le faire un Orc du clan Rochenoire. Mais cette voie est viciée. Nos marteaux sont faits pour créer des haches et des épées… pas ces machines issues des murmures de l\’étranger.', `VerifiedBuild`=26365 WHERE (`ID`=89108 AND `locale`='frFR');
