UPDATE `broadcast_text_locale` SET `Text_lang`='Les Draeneï…$B$B<Raza\’khul crache.>$B$BVous rendriez un grand service aux habitants de ce monde en les débarrassant des Draeneï.$B$BLe chemin qui s\’offre à vous est parsemé d\’ennemis qui ne chercheront qu\’à vous empêcher de mener votre mission à bien. Il ne peut y avoir qu\’une issue : la victoire ou la mort.$B$BÊtes-vous $gsûr:sûre; de votre choix ?', `VerifiedBuild`=26365 WHERE (`ID`=88720 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un bon choix. Les Humains sont la plaie de ce monde ; ils doivent être éliminés ou expulsés de Draenor.$B$BLe chemin qui s\’offre à vous est parsemé d\’ennemis qui ne chercheront qu\’à vous empêcher de mener votre mission à bien. Il ne peut y avoir qu\’une issue : la victoire ou la mort.$B$BÊtes-vous $gsûr:sûre; de votre choix ?', `VerifiedBuild`=26365 WHERE (`ID`=88719 AND `locale`='frFR');
