UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est lequel de nos chefs qu\’a eu la bonne idée d\’envoyer des Trolls des îles pour se battre dans la neige ?', `VerifiedBuild`=26365 WHERE (`ID`=80981 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tout à l\’heure, j\’ai essayé de boire à ma gourde. L\’eau, elle a gelé aussi sec. Il a fallu deux Orcs pour décoller ma gourde de ma langue. On dirait que ça leur a pas plu.', `VerifiedBuild`=26365 WHERE (`ID`=80979 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ah, vous voilà. Je pensais bien avoir senti l\’odeur d\’$gun:une:r; $R.', `VerifiedBuild`=26365 WHERE (`ID`=80684 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous affrontez votre mort, $R.', `VerifiedBuild`=26365 WHERE (`ID`=80626 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rien ne peut arrêter notre puissance ! Ni la Horde de Fer, ni les Draeneï. Nous sommes invincibles !', `Text1_lang`='Rien ne peut arrêter notre puissance ! Ni la Horde de Fer, ni les Draeneï. Nous sommes invincibles !', `VerifiedBuild`=26365 WHERE (`ID`=80571 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Draeneï ploieront devant notre puissante union, mes frères et sœurs !', `Text1_lang`='Les Draeneï ploieront devant notre puissante union, mes frères et sœurs !', `VerifiedBuild`=26365 WHERE (`ID`=80570 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Et pourquoi aurions-nous besoin de votre aide, « Elfe de sang » ? Les Auchenaï ont juré de protéger Auchindoun.', `VerifiedBuild`=26365 WHERE (`ID`=80501 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’ai juré de protéger cette tombe et les terres qui l\’entourent.$B$BNous combattrons jusqu\’au dernier pour empêcher ces Orcs de s\’emparer de l\’éclat des âmes !', `VerifiedBuild`=26365 WHERE (`ID`=80476 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pardonnez mon impolitesse, $gvoyageur:voyageuse;, je vous ai $gpris:prise; pour l\’ennemi.$B$BLe Conseil des ombres a tenté de piller ma maison à plusieurs reprises.$B$BLa première fois, ses agents ne s\’attendaient pas à une grande résistance de la part d\’un vieil ermite comme moi. J\’en ai tué trois en leur plantant une flèche dans la gorge.$B$BLa deuxième fois, je m\’étais préparé. Quatre Orcs sont arrivés et ils sont tous tombés dans mes pièges. Bande d\’imbéciles…$B$BMais je les ai mis en colère, et maintenant j\’attends qu\’ils reviennent.$B$BJ\’espère qu\’ils viendront plus nombreux. Ça fera d\’autant plus de victimes.', `VerifiedBuild`=26365 WHERE (`ID`=80424 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je pourrais manger un Orc.', `Text1_lang`='Je pourrais manger un Orc.', `VerifiedBuild`=26365 WHERE (`ID`=80419 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Auchindoun sera bientôt à nous. Malheureusement, vous ne serez plus là pour le voir, Elfe.', `Text1_lang`='Auchindoun sera bientôt à nous. Malheureusement, vous ne serez plus là pour le voir, Elfe.', `VerifiedBuild`=26365 WHERE (`ID`=80393 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais d\’abord, nous allons faire payer ces Orcs pour ce qu\’ils vous ont fait. Leurs péchés ne peuvent rester impunis.', `VerifiedBuild`=26365 WHERE (`ID`=80348 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous sommes des Draeneï, Yrel. C\’est dans notre nature de ne pas avoir de foyer.', `VerifiedBuild`=26365 WHERE (`ID`=80345 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous allons la retrouver, Hansel. Et nous allons faire des dégâts. Les Orcs vont payer pour ce qu\’ils ont fait à nos familles.', `VerifiedBuild`=26365 WHERE (`ID`=80334 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Aussi simple que de piquer ses bonbons à un Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=80232 AND `locale`='frFR');
