UPDATE `broadcast_text_locale` SET `Text_lang`='Les Draeneï vont connaître la défaite, et le monde entier se prosternera devant le Vide.', `VerifiedBuild`=26365 WHERE (`ID`=80105 AND `locale`='frFR');
